1.html code
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Loyalty Pro | Nfinite Edition</title>

    <!-- CSS Link -->
    <link rel="stylesheet" href="style.css">

    <!-- Fonts: Playfair Display (Headers) & Inter (Body) -->
    <link href="https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600&family=Playfair+Display:ital,wght@0,400;0,600;1,400&display=swap" rel="stylesheet">

    <!-- External Libraries -->
    <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css">
</head>
<body>

<!-- NOISE OVERLAY (Texture Effect) -->
<div class="noise-overlay"></div>

<!-- 1. INTRO CHOICE PAGE -->
<div id="choicePage" class="fullscreen-center">
    <div class="intro-container">
        <div class="anim-item delay-1">
            <i class="fas fa-infinity logo-main"></i>
        </div>
        <h1 class="anim-item delay-2">Loyalty <span class="serif-italic">Pro</span></h1>
        <p class="anim-item delay-3">Define your journey.</p>

        <div class="choice-buttons anim-item delay-4">
            <button onclick="handleNewUser()" class="btn-outline">
                <span>New User</span>
            </button>
            <button onclick="handleExistingUser()" class="btn-solid">
                <span>Existing User</span>
            </button>
        </div>
    </div>
</div>

<!-- 2. LOGIN PAGE (Hidden by default) -->
<div id="loginPage" class="fullscreen-center hidden">
    <div class="login-container anim-item delay-1">
        <button onclick="backToChoice()" class="back-link">
            <i class="fas fa-long-arrow-alt-left"></i> Return
        </button>

        <h2>Access Terminal</h2>
        <p class="sub-text">Please authenticate.</p>

        <div class="login-form">
            <div class="input-group">
                <label>Identifier</label>
                <input type="text" id="username" placeholder="Username">
            </div>

            <div class="input-group">
                <label>Passcode</label>
                <div class="password-wrapper">
                    <input type="password" id="password" placeholder="••••••">
                    <i class="fas fa-eye toggle-pass" onclick="togglePass()"></i>
                </div>
            </div>

            <button onclick="login()" class="btn-solid full-width">
                <span>Enter System</span>
            </button>
            <p id="loginError" class="error-text"></p>
        </div>
    </div>
</div>

<!-- 3. DASHBOARD (Hidden by default) -->
<div id="dashboard" class="hidden">

    <!-- TOP BAR -->
    <header class="top-bar">
        <div class="top-left">
            <button onclick="toggleSidebar()" class="sidebar-toggle">
                <i class="fas fa-bars"></i>
            </button>
            <div class="brand-text">Loyalty <span class="serif-italic">Pro</span></div>
        </div>
        <div class="top-right">
            <div id="userProfile" class="user-badge">Admin</div>

            <!-- Dynamic Sign In Button for Guests -->
            <button id="guestSignInBtn" onclick="handleExistingUser()" class="btn-small hidden">
                Sign In
            </button>

            <!-- Logout Button -->
            <button onclick="logout()" class="btn-small" style="margin-left: 10px; border-color: #444;">
                <i class="fas fa-power-off"></i> <span class="mobile-hide">Exit</span>
            </button>
        </div>
    </header>

    <div class="dashboard-layout">
        <!-- SIDEBAR (Collapsible) -->
        <aside class="sidebar" id="sidebar">
            <nav>
                <a href="#" class="nav-item active" data-section="overview">
                    <i class="fas fa-chart-line"></i> <span class="link-text">Overview</span>
                </a>
                <a href="#" class="nav-item" data-section="transaction">
                    <i class="fas fa-plus"></i> <span class="link-text">Transaction</span>
                </a>
                <a href="#" class="nav-item" data-section="search">
                    <i class="fas fa-search"></i> <span class="link-text">Search</span>
                </a>
                <a href="#" class="nav-item" data-section="customers">
                    <i class="fas fa-users"></i> <span class="link-text">Customers</span>
                </a>
                <a href="#" class="nav-item" data-section="analytics">
                    <i class="fas fa-chart-pie"></i> <span class="link-text">Analytics</span>
                </a>
            </nav>
        </aside>

        <!-- MAIN CONTENT -->
        <main class="main-content" id="mainContent">

            <!-- OVERVIEW SECTION -->
            <section id="overview" class="content-section active">
                <h1 class="page-title">System Overview</h1>
                <div class="grid-3">
                    <div class="card">
                        <span class="card-label">Total Users</span>
                        <div class="card-value" id="totalCustomers">0</div>
                    </div>
                    <div class="card">
                        <span class="card-label">Revenue</span>
                        <div class="card-value" id="totalRevenue">₹0</div>
                    </div>
                    <div class="card">
                        <span class="card-label">Active Points</span>
                        <div class="card-value" id="totalPoints">0</div>
                    </div>
                </div>
            </section>

            <!-- TRANSACTION SECTION -->
            <section id="transaction" class="content-section">
                <h1 class="page-title">New Entry</h1>
                <div class="card form-card">
                    <div class="input-row">
                        <div class="input-group">
                            <label>Mobile Number</label>
                            <input type="text" id="custMobile" placeholder="Search or Enter...">
                        </div>
                        <div class="input-group">
                            <label>Name</label>
                            <input type="text" id="custName" placeholder="Customer Name">
                        </div>
                    </div>

                    <!-- PIN SECTION -->
                    <div id="pinSection" class="pin-box hidden">
                        <div style="display:flex; justify-content:space-between; align-items:center; margin-bottom:10px;">
                            <label>Security PIN</label>
                            <span id="pinStatus" style="font-size:12px; color:#888;"></span>
                        </div>
                        <div style="display:flex; gap:10px;">
                            <input type="password" id="pinInput" class="pin-field" maxlength="4" placeholder="••••">
                            <button onclick="verifyPin()" class="btn-small">Verify</button>
                        </div>
                        <p id="pinError" style="color: #ff4d4d; font-size: 12px; margin-top:5px;"></p>
                    </div>

                    <!-- UPDATED TRANSACTION FORM (Bill vs Cash) -->
                    <div id="transactionForm" class="hidden" style="margin-top: 20px;">

                        <div class="input-row">
                            <div class="input-group">
                                <label>Bill Amount (₹)</label>
                                <input type="number" id="billAmount" placeholder="e.g. 900">
                            </div>
                            <div class="input-group">
                                <label>Cash Given (₹)</label>
                                <input type="number" id="cashGiven" placeholder="e.g. 1000">
                            </div>
                        </div>

                        <!-- Live Points Preview -->
                        <div style="background: rgba(255,255,255,0.05); padding: 15px; border-radius: 4px; margin-bottom: 20px; display: flex; justify-content: space-between; align-items: center; border: 1px solid #222;">
                            <span style="color: #888; font-size: 12px; text-transform: uppercase; letter-spacing: 1px;">Points to Add</span>
                            <span id="pointsPreview" style="font-size: 20px; font-weight: bold; color: #fff;">0</span>
                        </div>

                        <button id="submitTransaction" class="btn-solid full-width">Process Transaction</button>
                    </div>
                </div>
            </section>

            <!-- SEARCH SECTION -->
            <section id="search" class="content-section">
                <h1 class="page-title">Database Search</h1>
                <div class="card">
                    <div class="input-group">
                        <label>Find Customer</label>
                        <div style="display:flex; gap:10px;">
                            <input type="text" id="searchMobile" placeholder="Enter Mobile Number...">
                            <button onclick="initiateSearch()" class="btn-small">Search</button>
                        </div>
                    </div>
                    <div id="customerDetails" style="margin-top:20px;"></div>
                </div>
            </section>

            <!-- CUSTOMERS LIST SECTION -->
            <section id="customers" class="content-section">
                <h1 class="page-title">Registered Users</h1>
                <div class="card">
                    <div id="customerList"></div>
                </div>
            </section>

            <!-- ANALYTICS SECTION -->
            <!-- ANALYTICS SECTION (Deep Analysis) -->
<section id="analytics" class="content-section">
    <div style="display:flex; justify-content:space-between; align-items:flex-end; margin-bottom: 20px;">
        <h1 class="page-title" style="margin-bottom:0; border:none;">Intelligence Hub</h1>
        <button onclick="exportData()" class="btn-small"><i class="fas fa-download"></i> Export CSV</button>
    </div>

    <!-- 1. KPI CARDS -->
    <div class="grid-3" style="margin-bottom: 20px;">
        <div class="card analytics-card">
            <div class="icon-box"><i class="fas fa-wallet"></i></div>
            <div>
                <span class="card-label">Avg. Order Value (AOV)</span>
                <div class="card-value" style="font-size: 1.8rem;" id="aovValue">₹0</div>
            </div>
        </div>
        <div class="card analytics-card">
            <div class="icon-box"><i class="fas fa-coins"></i></div>
            <div>
                <span class="card-label">Points Liability</span>
                <div class="card-value" style="font-size: 1.8rem;" id="pointsLiability">0</div>
            </div>
        </div>
        <div class="card analytics-card">
            <div class="icon-box"><i class="fas fa-shopping-bag"></i></div>
            <div>
                <span class="card-label">Total Transactions</span>
                <div class="card-value" style="font-size: 1.8rem;" id="totalTxn">0</div>
            </div>
        </div>
    </div>

    <!-- 2. CHARTS ROW -->
    <div class="analytics-grid">
        <!-- Main Line Chart -->
        <div class="card wide-card">
            <h3 class="chart-title">Revenue Velocity</h3>
            <div class="chart-container">
                <canvas id="mainRevenueChart"></canvas>
            </div>
        </div>

        <!-- Doughnut Chart -->
        <div class="card narrow-card">
            <h3 class="chart-title">Customer Segments</h3>
            <div class="chart-container">
                <canvas id="segmentChart"></canvas>
            </div>
        </div>
    </div>

    <!-- 3. TOP CUSTOMERS TABLE -->
    <div class="card" style="margin-top: 20px;">
        <h3 class="chart-title">Top 5 "Whale" Customers</h3>
        <div id="topCustomersList"></div>
    </div>
</section>

        </main>
    </div>
</div>

<script src="script.js"></script>
</body>
</html>
2.css code
:root {
    --bg: #050505;           /* Deepest Black */
    --card-bg: #0a0a0a;      /* Almost Black */
    --border: #333333;       /* Subtle Border */
    --text-main: #e5e5e5;    /* Off-white Text */
    --text-muted: #888888;   /* Grey Text */
    --accent: #ffffff;       /* Pure White Accent */

    --font-serif: 'Playfair Display', serif;
    --font-sans: 'Inter', sans-serif;

    --sidebar-width: 240px;
    --sidebar-collapsed: 60px;
    --top-height: 60px;

    --transition-slow: all 0.6s cubic-bezier(0.16, 1, 0.3, 1);
    --transition-fast: all 0.3s ease;
}

* { margin: 0; padding: 0; box-sizing: border-box; }

body {
    background-color: var(--bg);
    color: var(--text-main);
    font-family: var(--font-sans);
    overflow-x: hidden;
}

/* NOISE OVERLAY */
.noise-overlay {
    position: fixed;
    top: 0; left: 0; width: 100%; height: 100%;
    pointer-events: none;
    z-index: 9999;
    opacity: 0.04;
    background: url('data:image/svg+xml,%3Csvg viewBox="0 0 200 200" xmlns="http://www.w3.org/2000/svg"%3E%3Cfilter id="noiseFilter"%3E%3CfeTurbulence type="fractalNoise" baseFrequency="0.65" numOctaves="3" stitchTiles="stitch"/%3E%3C/filter%3E%3Crect width="100%25" height="100%25" filter="url(%23noiseFilter)" opacity="1"/%3E%3C/svg%3E');
}

/* TYPOGRAPHY */
h1, h2, h3 { font-family: var(--font-serif); font-weight: 400; }
.serif-italic { font-family: var(--font-serif); font-style: italic; }

/* UTILS */
.hidden { display: none !important; }
.fullscreen-center {
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    position: relative;
    z-index: 10;
}

/* 1. ANIMATIONS */
.intro-container { text-align: center; }

.logo-main { font-size: 3rem; margin-bottom: 20px; color: var(--accent); }

.anim-item {
    opacity: 0;
    transform: translateY(30px);
    animation: fadeSlideUp 0.8s cubic-bezier(0.16, 1, 0.3, 1) forwards;
}

.delay-1 { animation-delay: 0.1s; }
.delay-2 { animation-delay: 0.3s; }
.delay-3 { animation-delay: 0.5s; }
.delay-4 { animation-delay: 0.7s; }

@keyframes fadeSlideUp {
    to { opacity: 1; transform: translateY(0); }
}

/* BUTTONS */
button { 
    cursor: pointer; 
    transition: var(--transition-fast); 
    font-family: var(--font-sans); 
    letter-spacing: 0.5px; 
    outline: none;
}

.choice-buttons {
    margin-top: 40px;
    display: flex;
    gap: 20px;
    justify-content: center;
}

.btn-outline {
    background: transparent;
    border: 1px solid var(--border);
    color: var(--text-main);
    padding: 12px 30px;
    border-radius: 4px;
}
.btn-outline:hover { border-color: var(--accent); transform: translateY(-2px); }

.btn-solid {
    background: var(--accent);
    color: var(--bg);
    border: 1px solid var(--accent);
    padding: 12px 30px;
    border-radius: 4px;
    font-weight: 600;
}
.btn-solid:hover { background: #ccc; border-color: #ccc; }

.btn-small {
    padding: 8px 16px;
    background: #222;
    color: #fff;
    border: 1px solid #333;
    font-size: 12px;
    border-radius: 4px;
}
.btn-small:hover { border-color: #666; background: #333; }

/* 2. LOGIN STYLES */
.login-container {
    width: 100%;
    max-width: 400px;
    padding: 40px;
    border: 1px solid var(--border);
    background: var(--card-bg);
    box-shadow: 0 20px 50px rgba(0,0,0,0.5);
}

.sub-text { color: var(--text-muted); font-size: 14px; margin-bottom: 30px; }

.input-group { margin-bottom: 20px; text-align: left; width: 100%; }
.input-group label { 
    display: block; 
    font-size: 11px; 
    color: var(--text-muted); 
    margin-bottom: 8px; 
    text-transform: uppercase; 
    letter-spacing: 1px; 
}
.input-group input {
    width: 100%;
    background: transparent;
    border: none;
    border-bottom: 1px solid var(--border);
    padding: 10px 0;
    color: white;
    font-size: 16px;
    outline: none;
    transition: 0.3s;
}
.input-group input:focus { border-color: var(--accent); }

.password-wrapper { position: relative; }
.toggle-pass {
    position: absolute;
    right: 0;
    top: 10px;
    color: var(--text-muted);
    cursor: pointer;
    padding: 5px;
}
.toggle-pass:hover { color: var(--text-main); }

.back-link {
    background: none;
    border: none;
    color: var(--text-muted);
    margin-bottom: 30px;
    display: block;
    font-size: 14px;
}
.back-link:hover { color: white; }

.full-width { width: 100%; margin-top: 10px; }

/* 3. DASHBOARD LAYOUT */
.top-bar {
    height: var(--top-height);
    background: rgba(10, 10, 10, 0.8);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid var(--border);
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 20px;
    position: fixed;
    top: 0; left: 0;
    width: 100%;
    z-index: 100;
}

.top-left { display: flex; align-items: center; gap: 20px; }
.brand-text { font-family: var(--font-serif); font-size: 1.2rem; letter-spacing: 1px; }
.sidebar-toggle { background: none; border: none; color: white; font-size: 18px; padding: 5px; }

.user-badge {
    border: 1px solid var(--border);
    padding: 5px 12px;
    border-radius: 20px;
    font-size: 12px;
    letter-spacing: 1px;
}

.dashboard-layout {
    display: flex;
    padding-top: var(--top-height);
    min-height: 100vh;
}

/* SIDEBAR COLLAPSIBLE */
.sidebar {
    width: var(--sidebar-width);
    background: var(--card-bg);
    border-right: 1px solid var(--border);
    height: calc(100vh - var(--top-height));
    position: fixed;
    top: var(--top-height);
    transition: var(--transition-slow);
    overflow: hidden;
    z-index: 90;
}

.sidebar.collapsed { width: var(--sidebar-collapsed); }

.nav-item {
    display: flex;
    align-items: center;
    padding: 20px 22px;
    color: var(--text-muted);
    text-decoration: none;
    transition: 0.2s;
    border-left: 2px solid transparent;
    white-space: nowrap;
}

.nav-item i { width: 20px; text-align: center; margin-right: 20px; font-size: 16px; }
.sidebar.collapsed .link-text { opacity: 0; pointer-events: none; }
.nav-item:hover, .nav-item.active { color: var(--accent); border-left-color: var(--accent); background: #111; }

/* MAIN CONTENT */
.main-content {
    margin-left: var(--sidebar-width);
    padding: 40px;
    width: 100%;
    transition: var(--transition-slow);
}

.main-content.expanded { margin-left: var(--sidebar-collapsed); }

.content-section { display: none; opacity: 0; animation: fadeSlideUp 0.6s forwards; }
.content-section.active { display: block; }

.page-title { 
    margin-bottom: 30px; 
    font-size: 2.5rem; 
    border-bottom: 1px solid var(--border); 
    padding-bottom: 20px; 
}

/* CARDS & GRIDS */
.grid-3 { display: grid; grid-template-columns: repeat(auto-fit, minmax(250px, 1fr)); gap: 20px; }

.card {
    background: var(--card-bg);
    border: 1px solid var(--border);
    padding: 30px;
    position: relative;
    transition: var(--transition-fast);
}
.card:hover { border-color: #444; }

.card-label { display: block; font-size: 12px; color: var(--text-muted); text-transform: uppercase; margin-bottom: 10px; }
.card-value { font-size: 2.5rem; font-family: var(--font-serif); }

.form-card { max-width: 600px; }
.input-row { display: flex; gap: 20px; margin-bottom: 10px; }

/* PIN BOX */
.pin-box {
    margin: 20px 0;
    border: 1px dashed var(--border);
    padding: 20px;
    background: rgba(255,255,255,0.02);
}

/* TABLE STYLES */
table { width: 100%; border-collapse: collapse; margin-top: 10px; }
th { text-align: left; color: var(--text-muted); font-size: 11px; text-transform: uppercase; letter-spacing: 1px; padding: 10px 0; border-bottom: 1px solid var(--border); }
td { padding: 15px 0; border-bottom: 1px solid #1a1a1a; font-size: 14px; color: var(--text-main); }
tr:last-child td { border-bottom: none; }

/* RESPONSIVE & UTILS */
@media (max-width: 768px) {
    .sidebar { width: var(--sidebar-collapsed); }
    .main-content { margin-left: var(--sidebar-collapsed); }
    .sidebar:hover { width: var(--sidebar-width); }
    .input-row { flex-direction: column; gap: 0; }
    .page-title { font-size: 2rem; }
}

@media (max-width: 480px) {
    .mobile-hide { display: none; }
    .user-badge { display: none; }
}
/* ANALYTICS SPECIFIC STYLES */
.analytics-card {
    display: flex;
    align-items: center;
    gap: 15px;
    padding: 20px;
}

.icon-box {
    width: 50px;
    height: 50px;
    border-radius: 50%;
    background: rgba(255,255,255,0.05);
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--accent);
    font-size: 20px;
    border: 1px solid #333;
}

.analytics-grid {
    display: grid;
    grid-template-columns: 2fr 1fr; /* 2/3 for Main chart, 1/3 for Donut */
    gap: 20px;
}

.wide-card { grid-column: span 1; height: 350px; }
.narrow-card { grid-column: span 1; height: 350px; }
.chart-container { position: relative; height: 280px; width: 100%; }

.chart-title {
    font-family: var(--font-serif);
    font-size: 16px;
    color: var(--text-muted);
    text-transform: uppercase;
    letter-spacing: 1px;
    margin-bottom: 15px;
}

/* Rank Table */
.rank-badge {
    background: #333;
    color: #fff;
    width: 25px;
    height: 25px;
    border-radius: 50%;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    font-size: 12px;
    font-weight: bold;
}
.rank-1 { background: #d4af37; color: #000; } /* Gold */
.rank-2 { background: #c0c0c0; color: #000; } /* Silver */
.rank-3 { background: #cd7f32; color: #000; } /* Bronze */

@media (max-width: 900px) {
    .analytics-grid { grid-template-columns: 1fr; }
    .wide-card, .narrow-card { height: auto; }
}

3. js code
// --- STATE & DATA ---
let customers = JSON.parse(localStorage.getItem('loyaltyDB')) || [];
let currentMobile = "";

// Chart Instances
let mainChart = null;
let segmentChart = null;

// --- ENTRY FLOW & NAVIGATION ---

// 1. Logout
function logout() {
    // Hide Dashboard
    const dashboard = document.getElementById('dashboard');
    dashboard.classList.add('hidden');

    // Hide Login Page
    document.getElementById('loginPage').classList.add('hidden');

    // Show Choice Page
    const choice = document.getElementById('choicePage');
    choice.classList.remove('hidden');

    // Reset Animation
    void choice.offsetWidth; 
    choice.style.opacity = '1';
    choice.style.transform = 'translateY(0)';

    // Clear forms
    document.getElementById('username').value = "";
    document.getElementById('password').value = "";
    document.getElementById('loginError').innerText = "";
    resetTransactionForm();
}

// 2. New User (Guest Mode)
function handleNewUser() {
    const choice = document.getElementById('choicePage');
    choice.style.opacity = '0';
    choice.style.transform = 'translateY(-20px)';
    choice.style.transition = 'all 0.5s ease';

    setTimeout(() => {
        choice.classList.add('hidden');
        document.getElementById('dashboard').classList.remove('hidden');
        document.getElementById('userProfile').innerText = "Guest Mode";
        document.getElementById('guestSignInBtn').classList.remove('hidden');
        initDashboard();
    }, 500);
}

// 3. Existing User (Login Mode)
function handleExistingUser() {
    const choice = document.getElementById('choicePage');
    choice.style.opacity = '0';

    setTimeout(() => {
        choice.classList.add('hidden');
        document.getElementById('dashboard').classList.add('hidden');
        const loginPage = document.getElementById('loginPage');
        loginPage.classList.remove('hidden');
        loginPage.querySelector('.login-container').style.animation = 'none';
        loginPage.querySelector('.login-container').offsetHeight; 
        loginPage.querySelector('.login-container').style.animation = 'fadeSlideUp 0.6s forwards';
    }, 300);
}

// 4. Back Button
function backToChoice() {
    document.getElementById('loginPage').classList.add('hidden');
    document.getElementById('choicePage').classList.remove('hidden');
    const choice = document.getElementById('choicePage');
    choice.style.opacity = '1';
    choice.style.transform = 'translateY(0)';
}

// 5. Password Toggle
function togglePass() {
    const field = document.getElementById('password');
    const icon = document.querySelector('.toggle-pass');
    if (field.type === 'password') {
        field.type = 'text';
        icon.classList.replace('fa-eye', 'fa-eye-slash');
    } else {
        field.type = 'password';
        icon.classList.replace('fa-eye-slash', 'fa-eye');
    }
}

// 6. Login Logic
function login() {
    const u = document.getElementById('username').value;
    const p = document.getElementById('password').value;

    if(u === "admin" && p === "admin") {
        document.getElementById('loginPage').classList.add('hidden');
        document.getElementById('dashboard').classList.remove('hidden');
        document.getElementById('userProfile').innerText = "Administrator";
        document.getElementById('guestSignInBtn').classList.add('hidden');
        initDashboard();
    } else {
        document.getElementById('loginError').innerText = "Credentials Invalid.";
    }
}

// 7. Sidebar Toggle
function toggleSidebar() {
    const sidebar = document.getElementById('sidebar');
    const content = document.getElementById('mainContent');
    sidebar.classList.toggle('collapsed');
    content.classList.toggle('expanded');
}

// 8. Navigation
document.querySelectorAll('.nav-item').forEach(item => {
    item.addEventListener('click', function(e) {
        e.preventDefault();
        document.querySelectorAll('.nav-item').forEach(i => i.classList.remove('active'));
        this.classList.add('active');
        const target = this.getAttribute('data-section');
        document.querySelectorAll('.content-section').forEach(s => s.classList.remove('active'));
        const activeSection = document.getElementById(target);
        activeSection.classList.add('active');
        activeSection.style.animation = 'none';
        activeSection.offsetHeight; 
        activeSection.style.animation = 'fadeSlideUp 0.6s forwards';
    });
});

// --- DASHBOARD & ANALYTICS FUNCTIONS ---

function initDashboard() {
    updateStats();          // Basic Stats (Top of Overview)
    calculateDeepMetrics(); // Advanced Analytics (Charts/Tables)
    renderCustomerList();   // Basic List
}

function updateStats() {
    document.getElementById('totalCustomers').innerText = customers.length;
    const rev = customers.reduce((acc, c) => acc + (c.totalSpent || 0), 0);
    document.getElementById('totalRevenue').innerText = '₹' + rev.toLocaleString();
    const pts = customers.reduce((acc, c) => acc + (c.points || 0), 0);
    document.getElementById('totalPoints').innerText = pts.toLocaleString();
}

// ==========================================
// NEW: ADVANCED ANALYTICS ENGINE
// ==========================================
function calculateDeepMetrics() {
    let totalRevenue = 0;
    let totalPoints = 0;
    let totalTxns = 0;

    customers.forEach(c => {
        totalRevenue += (c.totalSpent || 0);
        totalPoints += (c.points || 0);
        // Count total history items or fallback to legacy count
        totalTxns += (c.history ? c.history.length : (c.transactionCount || 0));
    });

    // 1. Calculate AOV (Average Order Value)
    const aov = totalTxns > 0 ? Math.round(totalRevenue / totalTxns) : 0;

    // Update KPI Cards
    if(document.getElementById('aovValue')) document.getElementById('aovValue').innerText = '₹' + aov;
    if(document.getElementById('pointsLiability')) document.getElementById('pointsLiability').innerText = totalPoints.toLocaleString();
    if(document.getElementById('totalTxn')) document.getElementById('totalTxn').innerText = totalTxns;

    // 2. Render Deep Charts
    renderMainChart();
    renderSegmentChart();
    renderTopCustomers();
}

// Chart 1: Revenue vs Points (Line Chart)
function renderMainChart() {
    const canvas = document.getElementById('mainRevenueChart');
    if (!canvas) return; // Guard clause if element doesn't exist
    const ctx = canvas.getContext('2d');

    if (mainChart) mainChart.destroy();

    // Simulation Data (In real app, group c.history by date)
    const labels = ['Week 1', 'Week 2', 'Week 3', 'Week 4'];
    const revenueData = [12000, 19000, 15000, 22000]; 
    const pointsData = [500, 1200, 800, 1500];

    mainChart = new Chart(ctx, {
        type: 'line',
        data: {
            labels: labels,
            datasets: [
                {
                    label: 'Revenue (₹)',
                    data: revenueData,
                    borderColor: '#ffffff',
                    backgroundColor: 'rgba(255, 255, 255, 0.1)',
                    borderWidth: 2,
                    tension: 0.4,
                    fill: true,
                    yAxisID: 'y'
                },
                {
                    label: 'Points',
                    data: pointsData,
                    borderColor: '#666',
                    borderDash: [5, 5],
                    borderWidth: 2,
                    tension: 0.4,
                    yAxisID: 'y1'
                }
            ]
        },
        options: {
            responsive: true,
            maintainAspectRatio: false,
            interaction: { mode: 'index', intersect: false },
            plugins: { legend: { labels: { color: '#888' } } },
            scales: {
                x: { grid: { color: '#222' }, ticks: { color: '#666' } },
                y: { type: 'linear', display: true, position: 'left', grid: { color: '#222' }, ticks: { color: '#666' } },
                y1: { type: 'linear', display: true, position: 'right', grid: { drawOnChartArea: false }, ticks: { color: '#444' } }
            }
        }
    });
}

// Chart 2: Customer Segments (Doughnut)
function renderSegmentChart() {
    const canvas = document.getElementById('segmentChart');
    if (!canvas) return;
    const ctx = canvas.getContext('2d');

    if (segmentChart) segmentChart.destroy();

    let low = 0, mid = 0, high = 0;
    customers.forEach(c => {
        if(c.totalSpent > 10000) high++;
        else if(c.totalSpent > 2000) mid++;
        else low++;
    });

    segmentChart = new Chart(ctx, {
        type: 'doughnut',
        data: {
            labels: ['VIP (>10k)', 'Regular (>2k)', 'New (<2k)'],
            datasets: [{
                data: [high, mid, low],
                backgroundColor: ['#ffffff', '#666666', '#222222'],
                borderWidth: 0
            }]
        },
        options: {
            responsive: true,
            maintainAspectRatio: false,
            plugins: { legend: { position: 'bottom', labels: { color: '#888' } } }
        }
    });
}

// Table: Top 5 Customers (Whales)
function renderTopCustomers() {
    const list = document.getElementById('topCustomersList');
    if(!list) return;

    const sorted = [...customers].sort((a, b) => b.totalSpent - a.totalSpent).slice(0, 5);

    if(sorted.length === 0) { list.innerHTML = "<p style='color:#666'>No data available.</p>"; return; }

    let html = `<table><thead><tr><th>Rank</th><th>Name</th><th>Spent</th><th>Points</th></tr></thead><tbody>`;

    sorted.forEach((c, index) => {
        const rankClass = index === 0 ? 'rank-1' : index === 1 ? 'rank-2' : index === 2 ? 'rank-3' : '';
        const rankDisplay = rankClass ? `<span class="rank-badge ${rankClass}">${index + 1}</span>` : `<span class="rank-badge" style="background:#222">${index + 1}</span>`;

        html += `
            <tr>
                <td>${rankDisplay}</td>
                <td>${c.name}</td>
                <td style="color:#fff; font-weight:bold;">₹${c.totalSpent.toLocaleString()}</td>
                <td style="color:#888;">${c.points}</td>
            </tr>
        `;
    });

    html += `</tbody></table>`;
    list.innerHTML = html;
}

// Export Data
function exportData() {
    let csv = "Name,Mobile,TotalSpent,Points\n";
    customers.forEach(c => {
        csv += `${c.name},${c.mobile},${c.totalSpent},${c.points}\n`;
    });
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'loyalty_data.csv';
    a.click();
}

// ==========================================
// TRANSACTION LOGIC (With History)
// ==========================================
const mobileInput = document.getElementById('custMobile');
const billInput = document.getElementById('billAmount');
const cashInput = document.getElementById('cashGiven');
const pointsPreview = document.getElementById('pointsPreview');

// 1. Mobile Search on Blur
mobileInput.addEventListener('blur', function() {
    const val = this.value;
    if(val.length < 10) return;

    currentMobile = val;
    const exists = customers.find(c => c.mobile === val);

    document.getElementById('pinSection').classList.remove('hidden');
    document.getElementById('transactionForm').classList.add('hidden');

    const pinStatus = document.getElementById('pinStatus');
    const pinTitle = document.getElementById('custName');

    if(exists) {
        pinTitle.value = exists.name;
        pinTitle.readOnly = true;
        pinStatus.innerText = "Existing User Verification";
    } else {
        pinTitle.value = "";
        pinTitle.readOnly = false;
        pinStatus.innerText = "Set New User PIN";
    }
});

// 2. Verify PIN
function verifyPin() {
    const pin = document.getElementById('pinInput').value;
    const exists = customers.find(c => c.mobile === currentMobile);
    const errorMsg = document.getElementById('pinError');

    if(exists) {
        if(exists.pin === pin) {
            document.getElementById('pinSection').classList.add('hidden');
            document.getElementById('transactionForm').classList.remove('hidden');
            errorMsg.innerText = "";
        } else {
            errorMsg.innerText = "Incorrect PIN code.";
        }
    } else {
        if(pin.length === 4) {
            alert("Security PIN Set. Proceed to transaction.");
            document.getElementById('pinSection').classList.add('hidden');
            document.getElementById('transactionForm').classList.remove('hidden');
            errorMsg.innerText = "";
        } else {
            errorMsg.innerText = "PIN must be 4 digits.";
        }
    }
}

// 3. Live Points Calculation Listener
function updatePointsPreview() {
    const bill = parseFloat(billInput.value) || 0;
    const cash = parseFloat(cashInput.value) || 0;

    if (cash >= bill && bill > 0) {
        const diff = cash - bill;
        pointsPreview.innerText = diff;
        pointsPreview.style.color = "#fff";
    } else {
        pointsPreview.innerText = "0";
        pointsPreview.style.color = "#555";
    }
}

billInput.addEventListener('input', updatePointsPreview);
cashInput.addEventListener('input', updatePointsPreview);

// 4. Submit Transaction (UPDATED FOR HISTORY)
document.getElementById('submitTransaction').addEventListener('click', function() {
    const name = document.getElementById('custName').value;
    const pin = document.getElementById('pinInput').value;

    // Get values from new inputs
    const bill = parseFloat(billInput.value);
    const cash = parseFloat(cashInput.value);

    // Validation
    if(!bill || bill <= 0) return alert("Please enter valid Bill Amount");
    if(!cash || cash < bill) return alert("Cash Given cannot be less than Bill Amount");

    // Logic: Points = Change (Cash - Bill)
    const earnedPoints = Math.floor(cash - bill);

    let c = customers.find(x => x.mobile === currentMobile);

    if(!c) {
        c = { 
            mobile: currentMobile, 
            name: name || "Guest", 
            pin: pin, 
            points: 0, 
            totalSpent: 0,
            transactionCount: 0,
            history: [] // New History Array
        };
        customers.push(c);
    }

    // Update Stats
    c.totalSpent += bill;    
    c.points += earnedPoints; 

    // SAVE HISTORY
    if(!c.history) c.history = []; // Initialize if old data
    c.history.push({
        date: new Date().toISOString(),
        bill: bill,
        points: earnedPoints
    });

    localStorage.setItem('loyaltyDB', JSON.stringify(customers));

    alert(`Transaction Successful! Added ${earnedPoints} Points.`);
    resetTransactionForm();
    initDashboard(); // Refresh all analytics
});

function resetTransactionForm() {
    document.getElementById('custMobile').value = "";
    document.getElementById('custName').value = "";
    document.getElementById('billAmount').value = "";
    document.getElementById('cashGiven').value = "";
    document.getElementById('pinInput').value = "";
    document.getElementById('pointsPreview').innerText = "0";
    document.getElementById('transactionForm').classList.add('hidden');
    document.getElementById('pinSection').classList.add('hidden');
}

// --- SEARCH LOGIC ---
function initiateSearch() {
    const mobile = document.getElementById('searchMobile').value;
    const c = customers.find(x => x.mobile === mobile);
    const div = document.getElementById('customerDetails');
    if(c) {
        div.innerHTML = `
            <div style="background:#111; padding:20px; border:1px solid #333; margin-top:20px;">
                <h3 style="margin-bottom:10px;">${c.name}</h3>
                <p style="color:#888;">Points: <span style="color:#fff;">${c.points}</span></p>
                <p style="color:#888;">Total Spent: <span style="color:#fff;">₹${c.totalSpent}</span></p>
            </div>
        `;
    } else {
        div.innerHTML = `<p style="color:red; margin-top:10px;">User not found in database.</p>`;
    }
}

// --- RENDER LIST ---
function renderCustomerList() {
    const list = document.getElementById('customerList');
    if(customers.length === 0) { 
        list.innerHTML = "<p style='color:#888;'>No records found.</p>"; 
        return; 
    }
    let h = `<table><thead><tr><th>Name</th><th>Mobile</th><th>Points</th><th>Total Spend</th></tr></thead><tbody>`;
    customers.forEach(c => {
        h += `<tr><td>${c.name}</td><td style="font-family:monospace;">${c.mobile}</td><td>${c.points}</td><td>₹${c.totalSpent}</td></tr>`;
    });
    h += `</tbody></table>`;
    list.innerHTML = h;
}

