        setActiveContentKey(prev => prev + 1); // Force re-mount for animation
    };

    const dashboardStats = useMemo(() => {
        const totalCustomers = customers.length;
        const totalRevenue = customers.reduce((acc, c) => acc + (c.totalSpent || 0), 0);
        const totalPoints = customers.reduce((acc, c) => acc + (c.points || 0), 0);
        return { totalCustomers, totalRevenue, totalPoints };
    }, [customers]);

    const renderSection = () => {
        switch (activeSection) {
            case Section.Transaction:
                return <TransactionSection customers={customers} setCustomers={setCustomers} />;
            case Section.Search:
                return <SearchSection customers={customers} />;
            case Section.Customers:
                return <CustomersSection customers={customers} />;
            case Section.Analytics:
                return <AnalyticsSection customers={customers} />;
            case Section.Overview:
            default:
                return <OverviewSection stats={dashboardStats} />;
        }
    };

    return (
        <div className="min-h-screen flex flex-col">
            <header className="h-[60px] bg-brand-card/80 backdrop-blur-lg border-b border-brand-border flex justify-between items-center px-5 fixed top-0 left-0 w-full z-50">
                <div className="flex items-center gap-5">
                    <button onClick={() => setIsSidebarCollapsed(!isSidebarCollapsed)} className="text-white text-lg p-1">
                        <FaBars />
                    </button>
                    <div className="font-serif text-lg tracking-wider">Loyalty <span className="italic">Pro</span></div>
                </div>
                <div className="flex items-center gap-3">
                    <div className="border border-brand-border py-1 px-4 rounded-full text-xs tracking-widest hidden sm:block">
                        {userType === UserType.Admin ? 'Administrator' : 'Guest Mode'}
                    </div>
                    {userType === UserType.Guest && (
                        <button onClick={handleSignIn} className="bg-neutral-800 text-white border border-neutral-700 text-xs py-1.5 px-4 rounded-md hover:border-neutral-500 hover:bg-neutral-700 transition-colors">
                            Sign In
                        </button>
                    )}
                    <button onClick={handleLogout} className="bg-neutral-800 text-white border border-neutral-700 text-xs py-1.5 px-4 rounded-md flex items-center gap-2 hover:border-neutral-500 hover:bg-neutral-700 transition-colors">
                        <FaPowerOff />
                        <span className="hidden md:inline">Exit</span>
                    </button>
                </div>
            </header>

            <div className="flex pt-[60px] flex-1">
                <aside className={`bg-brand-card border-r border-brand-border h-[calc(100vh-60px)] fixed top-[60px] transition-all duration-500 ease-[cubic-bezier(0.16,1,0.3,1)] z-40 ${isSidebarCollapsed ? 'w-[60px]' : 'w-[240px]'}`}>
                    <nav className="mt-4">
                        {(Object.values(Section)).map(section => (
                            <NavItem
                                key={section}
                                section={section}
                                activeSection={activeSection}
                                isCollapsed={isSidebarCollapsed}
                                onClick={handleNavClick}
                            />
                        ))}
                    </nav>
                </aside>

                <main className={`flex-1 transition-all duration-500 ease-[cubic-bezier(0.16,1,0.3,1)] p-4 sm:p-6 md:p-10 ${isSidebarCollapsed ? 'ml-[60px]' : 'ml-[240px]'}`}>
                    <div key={activeContentKey} className="animate-fade-slide-up" style={{ animationDuration: '0.6s' }}>
                        {renderSection()}
                    </div>
                </main>
            </div>
        </div>
    );
};

const navIcons: { [key in Section]: React.ElementType } = {
    [Section.Overview]: FaChartLine,
    [Section.Transaction]: FaPlus,
    [Section.Search]: FaSearch,
    [Section.Customers]: FaUsers,
    [Section.Analytics]: FaChartPie,
};

const NavItem: React.FC<{ section: Section, activeSection: Section, isCollapsed: boolean, onClick: (s: Section) => void }> = ({ section, activeSection, isCollapsed, onClick }) => {
    const Icon = navIcons[section];
    const isActive = section === activeSection;

    return (
        <a
            href="#"
            onClick={(e) => { e.preventDefault(); onClick(section); }}
            className={`flex items-center h-16 text-brand-muted hover:text-brand-accent hover:bg-neutral-900 transition-all border-l-2 ${isActive ? 'border-brand-accent text-brand-accent bg-neutral-900' : 'border-transparent'}`}
        >
            <div className="w-[60px] flex justify-center items-center text-lg"><Icon /></div>
            <span className={`capitalize whitespace-nowrap transition-opacity duration-300 ${isCollapsed ? 'opacity-0' : 'opacity-100'}`}>{section}</span>
        </a>
    );
};

const PageTitle: React.FC<{ children: React.ReactNode, className?: string }> = ({ children, className }) => (
    <h1 className={`font-serif text-3xl md:text-4xl mb-8 pb-5 border-b border-brand-border ${className}`}>{children}</h1>
);


const OverviewSection: React.FC<{ stats: { totalCustomers: number; totalRevenue: number; totalPoints: number; } }> = ({ stats }) => (
    <section>
        <PageTitle>System Overview</PageTitle>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            <StatCard label="Total Users" value={stats.totalCustomers.toLocaleString()} />
            <StatCard label="Revenue" value={`₹${stats.totalRevenue.toLocaleString()}`} />
            <StatCard label="Active Points" value={stats.totalPoints.toLocaleString()} />
        </div>
    </section>
);


const TransactionSection: React.FC<{ customers: Customer[], setCustomers: React.Dispatch<React.SetStateAction<Customer[]>> }> = ({ customers, setCustomers }) => {
    const [mobile, setMobile] = useState('');
    const [name, setName] = useState('');
    const [pin, setPin] = useState('');
    const [billAmount, setBillAmount] = useState('');
    const [cashGiven, setCashGiven] = useState('');

    const [currentCustomer, setCurrentCustomer] = useState<Customer | null>(null);
    const [isNewCustomer, setIsNewCustomer] = useState(false);

    const [showPinSection, setShowPinSection] = useState(false);
    const [showTransactionForm, setShowTransactionForm] = useState(false);

    const [pinStatus, setPinStatus] = useState('');
    const [pinError, setPinError] = useState('');

    const custNameRef = useRef<HTMLInputElement>(null);

    const resetForm = useCallback(() => {
        setMobile('');
        setName('');
        setPin('');
        setBillAmount('');
        setCashGiven('');
        setCurrentCustomer(null);
        setIsNewCustomer(false);
        setShowPinSection(false);
        setShowTransactionForm(false);
        setPinStatus('');
        setPinError('');
    }, []);

    useEffect(() => {
        if(mobile.length >= 10) {
            const existingCustomer = customers.find(c => c.mobile === mobile);
            setCurrentCustomer(existingCustomer || null);
            setShowPinSection(true);
            setShowTransactionForm(false);
            if (existingCustomer) {
                setName(existingCustomer.name);
                setIsNewCustomer(false);
                setPinStatus("Existing User Verification");
            } else {
                setName('');
                setIsNewCustomer(true);
                setPinStatus("Set New User PIN");
                custNameRef.current?.focus();
            }
        } else {
            setShowPinSection(false);
            setShowTransactionForm(false);
            setCurrentCustomer(null);
            setName('');
        }
    }, [mobile, customers]);

    const handlePinVerify = () => {
        setPinError('');
        if (isNewCustomer) {
            if (pin.length === 4) {
                setShowPinSection(false);
                setShowTransactionForm(true);
            } else {
                setPinError("PIN must be 4 digits.");
            }
        } else {
            if (currentCustomer?.pin === pin) {
                setShowPinSection(false);
                setShowTransactionForm(true);
            } else {
                setPinError("Incorrect PIN code.");
            }
        }
    };

    const handleTransactionSubmit = () => {
        const bill = parseFloat(billAmount);
        const cash = parseFloat(cashGiven);

        if (!bill || bill <= 0) { alert("Please enter a valid Bill Amount"); return; }
        if (!cash || cash < bill) { alert("Cash Given cannot be less than Bill Amount"); return; }

        const earnedPoints = Math.floor(cash - bill);
        const newHistoryEntry: TransactionHistory = { date: new Date().toISOString(), bill, points: earnedPoints };

        if (isNewCustomer) {
            const newCustomer: Customer = {
                mobile,
                name: name || "Guest",
                pin,
                points: earnedPoints,
                totalSpent: bill,
                history: [newHistoryEntry]
            };
            setCustomers(prev => [...prev, newCustomer]);
        } else {
            setCustomers(prev => prev.map(c => 
                c.mobile === mobile 
                ? { ...c, totalSpent: c.totalSpent + bill, points: c.points + earnedPoints, history: [...c.history, newHistoryEntry] } 
                : c
            ));
        }

        alert(`Transaction Successful! Added ${earnedPoints} Points.`);
        resetForm();
    };

    const pointsPreview = useMemo(() => {
        const bill = parseFloat(billAmount) || 0;
        const cash = parseFloat(cashGiven) || 0;
        return (cash >= bill && bill > 0) ? Math.floor(cash - bill) : 0;
    }, [billAmount, cashGiven]);

    return (
        <section>
            <PageTitle>New Entry</PageTitle>
            <Card className="max-w-xl mx-auto">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                        <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Mobile Number</label>
                        <input type="text" value={mobile} onChange={e => setMobile(e.target.value)} placeholder="Search or Enter..." className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors" />
                    </div>
                    <div>
                        <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Name</label>
                        <input ref={custNameRef} type="text" value={name} onChange={e => setName(e.target.value)} placeholder="Customer Name" readOnly={!isNewCustomer} className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors read-only:bg-neutral-800/30" />
                    </div>
                </div>

                {showPinSection && (
                    <div className="mt-6 border-t border-dashed border-brand-border pt-6">
                        <div className="flex justify-between items-center mb-2">
                            <label className="text-sm text-brand-text">Security PIN</label>
                            <span className="text-xs text-brand-muted">{pinStatus}</span>
                        </div>
                        <div className="flex gap-4">
                            <input type="password" value={pin} onChange={e => setPin(e.target.value)} maxLength={4} placeholder="••••" className="flex-grow w-full bg-neutral-900 border border-brand-border py-2 px-3 text-white text-lg tracking-[0.5em] text-center outline-none focus:border-brand-accent transition-colors rounded-md" />
                            <button onClick={handlePinVerify} className="bg-brand-accent text-brand-bg py-2 px-6 rounded-md font-semibold text-sm">Verify</button>
                        </div>
                        {pinError && <p className="text-red-500 text-xs mt-2">{pinError}</p>}
                    </div>
                )}

                {showTransactionForm && (
                     <div className="mt-6 border-t border-brand-border pt-6 space-y-6">
                         <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            <div>
                                <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Bill Amount (₹)</label>
                                <input type="number" value={billAmount} onChange={e => setBillAmount(e.target.value)} placeholder="e.g. 900" className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors" />
                            </div>
                            <div>
                                <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Cash Given (₹)</label>
                                <input type="number" value={cashGiven} onChange={e => setCashGiven(e.target.value)} placeholder="e.g. 1000" className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors" />
                            </div>
                         </div>
                         <div className="bg-neutral-900/50 p-4 border border-brand-border rounded-md flex justify-between items-center">
                            <span className="text-xs text-brand-muted uppercase tracking-wider">Points to Add</span>
                            <span className={`text-2xl font-bold ${pointsPreview > 0 ? 'text-white' : 'text-neutral-600'}`}>{pointsPreview}</span>
                         </div>
                         <button onClick={handleTransactionSubmit} className="w-full bg-brand-accent text-brand-bg py-3 rounded-md font-semibold">Process Transaction</button>
                     </div>
                )}
            </Card>
        </section>
    );
};


const SearchSection: React.FC<{ customers: Customer[] }> = ({ customers }) => {
    const [searchMobile, setSearchMobile] = useState('');
    const [result, setResult] = useState<Customer | null | undefined>(undefined);

    const handleSearch = () => {
        const found = customers.find(c => c.mobile === searchMobile);
        setResult(found || null);
    };

    return (
        <section>
            <PageTitle>Database Search</PageTitle>
            <Card className="max-w-xl mx-auto">
                <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Find Customer</label>
                <div className="flex gap-4">
                    <input type="text" value={searchMobile} onChange={e => setSearchMobile(e.target.value)} placeholder="Enter Mobile Number..." className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors" />
                    <button onClick={handleSearch} className="bg-neutral-800 text-white border border-neutral-700 py-2 px-6 rounded-md font-semibold text-sm hover:border-neutral-500 hover:bg-neutral-700 transition-colors">Search</button>
                </div>

                {result !== undefined && (
                    <div className="mt-6">
                        {result ? (
                            <div className="bg-neutral-900/50 border border-brand-border p-5 rounded-md">
                                <h3 className="text-xl font-serif mb-2">{result.name}</h3>
                                <p className="text-brand-muted">Points: <span className="text-white font-semibold">{result.points.toLocaleString()}</span></p>
                                <p className="text-brand-muted">Total Spent: <span className="text-white font-semibold">₹{result.totalSpent.toLocaleString()}</span></p>
                            </div>
                        ) : (
                            <p className="text-red-500">User not found in database.</p>
                        )}
                    </div>
                )}
            </Card>
        </section>
    );
};


const CustomersSection: React.FC<{ customers: Customer[] }> = ({ customers }) => (
    <section>
        <PageTitle>Registered Users</PageTitle>
        <Card>
            <div className="overflow-x-auto">
                {customers.length > 0 ? (
                    <table className="w-full min-w-[600px]">
                        <thead>
                            <tr>
                                <th className="text-left text-xs text-brand-muted uppercase tracking-wider pb-3 border-b border-brand-border">Name</th>
                                <th className="text-left text-xs text-brand-muted uppercase tracking-wider pb-3 border-b border-brand-border">Mobile</th>
                                <th className="text-left text-xs text-brand-muted uppercase tracking-wider pb-3 border-b border-brand-border">Points</th>
                                <th className="text-left text-xs text-brand-muted uppercase tracking-wider pb-3 border-b border-brand-border">Total Spend</th>
                            </tr>
                        </thead>
                        <tbody>
                            {customers.map(c => (
                                <tr key={c.mobile}>
                                    <td className="py-4 border-b border-neutral-800">{c.name}</td>
                                    <td className="py-4 border-b border-neutral-800 font-mono">{c.mobile}</td>
                                    <td className="py-4 border-b border-neutral-800">{c.points.toLocaleString()}</td>
                                    <td className="py-4 border-b border-neutral-800">₹{c.totalSpent.toLocaleString()}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                ) : <p className="text-brand-muted">No records found.</p>}
            </div>
        </Card>
    </section>
);

const AnalyticsSection: React.FC<{ customers: Customer[] }> = ({ customers }) => {
    const analyticsData = useMemo(() => {
        let totalRevenue = 0;
        let totalPoints = 0;
        let totalTxns = 0;
        let lowTier = 0, midTier = 0, highTier = 0;

        customers.forEach(c => {
            totalRevenue += c.totalSpent || 0;
            totalPoints += c.points || 0;
            totalTxns += c.history?.length || 0;

            if (c.totalSpent > 10000) highTier++;
            else if (c.totalSpent > 2000) midTier++;
            else lowTier++;
        });

        const aov = totalTxns > 0 ? Math.round(totalRevenue / totalTxns) : 0;

        const topCustomers = [...customers].sort((a, b) => b.totalSpent - a.totalSpent).slice(0, 5);

        // Dummy data for revenue chart
        const revenueChartData = [
            { name: 'Week 1', Revenue: 12000, Points: 500 },
            { name: 'Week 2', Revenue: 19000, Points: 1200 },
            { name: 'Week 3', Revenue: 15000, Points: 800 },
            { name: 'Week 4', Revenue: 22000, Points: 1500 },
        ];

        const segmentChartData = [
            { name: 'VIP (>10k)', value: highTier },
            { name: 'Regular (>2k)', value: midTier },
            { name: 'New (<2k)', value: lowTier },
        ];
        const COLORS = ['#FFFFFF', '#666666', '#222222'];

        return { aov, totalPoints, totalTxns, topCustomers, revenueChartData, segmentChartData, COLORS };
    }, [customers]);

    return (
        <section>
            <div className="flex flex-col sm:flex-row justify-between sm:items-end mb-8">
                <PageTitle className="mb-0 border-none">Intelligence Hub</PageTitle>
                <button onClick={() => exportDataToCsv(customers)} className="bg-neutral-800 text-white border border-neutral-700 text-xs py-2 px-4 rounded-md flex items-center gap-2 hover:border-neutral-500 hover:bg-neutral-700 transition-colors mt-4 sm:mt-0">
                    <FaDownload /> Export CSV
                </button>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-6">
                 <AnalyticsStatCard icon={FaWallet} label="Avg. Order Value (AOV)" value={`₹${analyticsData.aov.toLocaleString()}`} />
                 <AnalyticsStatCard icon={FaCoins} label="Points Liability" value={analyticsData.totalPoints.toLocaleString()} />
                 <AnalyticsStatCard icon={FaShoppingBag} label="Total Transactions" value={analyticsData.totalTxns.toLocaleString()} />
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                <Card className="lg:col-span-2 h-[350px]">
                    <h3 className="chart-title">Revenue Velocity</h3>
                    <ResponsiveContainer width="100%" height="90%">
                        <LineChart data={analyticsData.revenueChartData}>
                            <CartesianGrid strokeDasharray="3 3" stroke="#333" />
                            <XAxis dataKey="name" stroke="#888" fontSize={12} />
                            <YAxis yAxisId="left" stroke="#888" fontSize={12} />
                            <YAxis yAxisId="right" orientation="right" stroke="#666" fontSize={12} />
                            <Tooltip contentStyle={{ backgroundColor: '#0a0a0a', border: '1px solid #333' }} />
                            <Legend wrapperStyle={{fontSize: "12px", color: '#888'}} />
                            <Line yAxisId="left" type="monotone" dataKey="Revenue" stroke="#ffffff" strokeWidth={2} />
                            <Line yAxisId="right" type="monotone" dataKey="Points" stroke="#666" strokeDasharray="5 5" />
                        </LineChart>
                    </ResponsiveContainer>
                </Card>
                <Card className="h-[350px]">
                     <h3 className="chart-title">Customer Segments</h3>
                     <ResponsiveContainer width="100%" height="90%">
                        <PieChart>
                            <Pie data={analyticsData.segmentChartData} cx="50%" cy="50%" innerRadius={60} outerRadius={80} fill="#8884d8" paddingAngle={5} dataKey="value">
                                {analyticsData.segmentChartData.map((entry, index) => (
                                    <Cell key={`cell-${index}`} fill={analyticsData.COLORS[index % analyticsData.COLORS.length]} />
                                ))}
                            </Pie>
                            <Tooltip contentStyle={{ backgroundColor: '#0a0a0a', border: '1px solid #333' }}/>
                            <Legend wrapperStyle={{fontSize: "12px", color: '#888'}} />
                        </PieChart>
                    </ResponsiveContainer>
                </Card>
            </div>
             <Card className="mt-6">
                <h3 className="chart-title">Top 5 "Whale" Customers</h3>
                <div className="overflow-x-auto">
                    {analyticsData.topCustomers.length > 0 ? (
                    <table className="w-full min-w-[500px]">
                        <thead>
                            <tr>
                                <th className="text-left text-xs text-brand-muted uppercase tracking-wider pb-3 border-b border-brand-border">Rank</th>
                                <th className="text-left text-xs text-brand-muted uppercase tracking-wider pb-3 border-b border-brand-border">Name</th>
                                <th className="text-left text-xs text-brand-muted uppercase tracking-wider pb-3 border-b border-brand-border">Spent</th>
                                <th className="text-left text-xs text-brand-muted uppercase tracking-wider pb-3 border-b border-brand-border">Points</th>
                            </tr>
                        </thead>
                        <tbody>
                            {analyticsData.topCustomers.map((c, index) => {
                                const rankColors = ['bg-yellow-400 text-black', 'bg-gray-400 text-black', 'bg-yellow-700 text-white'];
                                const rankClass = rankColors[index] || 'bg-neutral-700 text-white';
                                return (
                                <tr key={c.mobile}>
                                    <td className="py-3 border-b border-neutral-800"><span className={`w-6 h-6 rounded-full flex items-center justify-center text-xs font-bold ${rankClass}`}>{index + 1}</span></td>
                                    <td className="py-3 border-b border-neutral-800">{c.name}</td>
                                    <td className="py-3 border-b border-neutral-800 font-bold text-white">₹{c.totalSpent.toLocaleString()}</td>
                                    <td className="py-3 border-b border-neutral-800 text-brand-muted">{c.points.toLocaleString()}</td>
                                </tr>
                            )})}
                        </tbody>
                    </table>
                     ) : <p className="text-brand-muted">No data available.</p>}
                </div>
            </Card>
        </section>
    );
};

const AnalyticsStatCard: React.FC<{ icon: React.ElementType, label: string, value: string | number }> = ({ icon: Icon, label, value }) => (
    <Card className="flex items-center gap-4 p-5">
        <div className="w-12 h-12 rounded-full bg-neutral-900/50 border border-brand-border flex items-center justify-center text-xl text-brand-accent flex-shrink-0">
            <Icon />
        </div>
        <div>
            <span className="block text-xs text-brand-muted uppercase">{label}</span>
            <div className="text-2xl font-semibold text-brand-accent">{value}</div>
        </div>
    </Card>
);

export default Dashboard;

// FIX: Import Dispatch and SetStateAction to resolve 'Cannot find namespace React' error.
import { useState, useEffect, Dispatch, SetStateAction } from 'react';

type SetValue<T> = Dispatch<SetStateAction<T>>;

export function useLocalStorage<T>(key: string, initialValue: T): [T, SetValue<T>, () => void] {
  const readValue = (): T => {
    if (typeof window === 'undefined') {
      return initialValue;
    }
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      console.warn(`Error reading localStorage key “${key}”:`, error);
      return initialValue;
    }
  };

  const [storedValue, setStoredValue] = useState<T>(readValue);

  const setValue: SetValue<T> = (value) => {
    try {
      const valueToStore = value instanceof Function ? value(storedValue) : value;
      setStoredValue(valueToStore);
      if (typeof window !== 'undefined') {
        window.localStorage.setItem(key, JSON.stringify(valueToStore));
      }
    } catch (error) {
      console.warn(`Error setting localStorage key “${key}”:`, error);
    }
  };

  const clearValue = () => {
    try {
      if (typeof window !== 'undefined') {
        window.localStorage.removeItem(key);
        setStoredValue(initialValue);
      }
    } catch (error) {
      console.warn(`Error clearing localStorage key “${key}”:`, error);
    }
  }

  useEffect(() => {
    setStoredValue(readValue());
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return [storedValue, setValue, clearValue];
}

