const App: React.FC = () => {
    const [page, setPage] = useState<Page>(Page.Choice);
    const [userType, setUserType] = useState<UserType | null>(null);
    const [loginError, setLoginError] = useState('');
    const [customers, setCustomers, clearCustomers] = useLocalStorage<Customer[]>('loyaltyDB', []);
    const [tierSettings, setTierSettings] = useLocalStorage<TierSettings>('loyaltySettings', DEFAULT_TIER_SETTINGS);

    const handleLogin = useCallback((u: string, p: string) => {
        if (u === 'admin' && p === 'admin') {
            setUserType(UserType.Admin);
            setPage(Page.Dashboard);
            setLoginError('');
        } else {
            setLoginError('Credentials Invalid.');
        }
    }, []);

    const handleGuestLogin = useCallback(() => {
        setUserType(UserType.Guest);
        setPage(Page.Dashboard);
    }, []);


    const handleLogout = useCallback(() => {
        setUserType(null);
        setPage(Page.Choice);
    }, []);

    const handleSelectExisting = useCallback(() => {
        setPage(Page.Login);
    }, []);

    const renderPage = () => {
        switch (page) {
