                          handleSignIn={handleSelectExisting}
                       />;
            case Page.Choice:
            default:
                return <ChoicePage onSelectGuest={handleGuestLogin} onSelectExisting={handleSelectExisting} />;
        }
    };

    return (
        <>
            <NoiseOverlay />
            <main className="min-h-screen">
                {renderPage()}
            </main>
        </>
    );
};

