// Tier utility functions
export const getCustomerTier = (customer: Customer, settings: TierSettings): Tier => {
    if (customer.totalSpent >= settings.platinum.minSpend && customer.points >= settings.platinum.minPoints) {
        return 'Platinum';
    }
    if (customer.totalSpent >= settings.gold.minSpend && customer.points >= settings.gold.minPoints) {
        return 'Gold';
    }
    if (customer.totalSpent >= settings.silver.minSpend && customer.points >= settings.silver.minPoints) {
        return 'Silver';
    }
    return 'Bronze';
};

