                 <AnalyticsStatCard icon={FaWallet} label="Avg. Order Value (AOV)" value={`₹${analyticsData.aov.toLocaleString()}`} />
                 <AnalyticsStatCard icon={FaCoins} label="Points Liability" value={analyticsData.totalPoints.toLocaleString()} />
                 <AnalyticsStatCard icon={FaShoppingBag} label="Total Transactions" value={analyticsData.totalTxns.toLocaleString()} />
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                <Card className="lg:col-span-2 h-[350px]">
                    <h3 className="chart-title">Revenue Velocity</h3>
                    <ResponsiveContainer width="100%" height="90%">
