                    <nav className="mt-4">
                        {(Object.values(Section)).map(section => {
                            if (section === Section.Settings && !currentUser) {
                                return null;
                            }
                            return (
                                <NavItem
                                    key={section}
                                    section={section}
                                    activeSection={activeSection}
                                    isCollapsed={isSidebarCollapsed}
                                    onClick={handleNavClick}
                                />
