const AnalyticsSection: React.FC<{ customers: Customer[]; tierSettings: TierSettings; }> = ({ customers, tierSettings }) => {
    const analyticsData = useMemo(() => {
        let totalRevenue = 0;
        let totalPoints = 0;
        let totalTxns = 0;

        const tierCounts: Record<Tier, number> = { Bronze: 0, Silver: 0, Gold: 0, Platinum: 0 };

        customers.forEach(c => {
            totalRevenue += c.totalSpent || 0;
            totalPoints += c.points || 0;
            totalTxns += c.history?.length || 0;
            const tier = getCustomerTier(c, tierSettings);
            tierCounts[tier]++;
        });

        const aov = totalTxns > 0 ? Math.round(totalRevenue / totalTxns) : 0;

        const topCustomers = [...customers].sort((a, b) => b.totalSpent - a.totalSpent).slice(0, 5);

        // Dummy data for revenue chart
        const revenueChartData = [
            { name: 'Week 1', Revenue: 12000, Points: 500 },
            { name: 'Week 2', Revenue: 19000, Points: 1200 },
            { name: 'Week 3', Revenue: 15000, Points: 800 },
            { name: 'Week 4', Revenue: 22000, Points: 1500 },
        ];

        const segmentChartData = [
            { name: 'Platinum', value: tierCounts.Platinum },
            { name: 'Gold', value: tierCounts.Gold },
            { name: 'Silver', value: tierCounts.Silver },
            { name: 'Bronze', value: tierCounts.Bronze },
        ].filter(item => item.value > 0);

        const COLORS = ['#e9d5ff', '#fde047', '#d1d5db', '#ca8a04']; // Platinum, Gold, Silver, Bronze

        return { aov, totalPoints, totalTxns, topCustomers, revenueChartData, segmentChartData, COLORS };
    }, [customers, tierSettings]);

    return (
        <section>
            <div className="flex flex-col sm:flex-row justify-between sm:items-end mb-8">
                <PageTitle className="mb-0 border-none">Intelligence Hub</PageTitle>
                <button onClick={() => exportDataToCsv(customers)} className="bg-neutral-800 text-white border border-neutral-700 text-xs py-2 px-4 rounded-md flex items-center gap-2 hover:border-neutral-500 hover:bg-neutral-700 transition-colors mt-4 sm:mt-0">
                    <FaDownload /> Export CSV
                </button>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-6">
                 <AnalyticsStatCard icon={FaWallet} label="Avg. Order Value (AOV)" value={`₹${analyticsData.aov.toLocaleString()}`} />
                 <AnalyticsStatCard icon={FaCoins} label="Points Liability" value={analyticsData.totalPoints.toLocaleString()} />
                 <AnalyticsStatCard icon={FaShoppingBag} label="Total Transactions" value={analyticsData.totalTxns.toLocaleString()} />
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                <Card className="lg:col-span-2 h-[350px]">
                    <h3 className="chart-title">Revenue Velocity</h3>
                    <ResponsiveContainer width="100%" height="90%">
                        <LineChart data={analyticsData.revenueChartData}>
                            <CartesianGrid strokeDasharray="3 3" stroke="#333" />
                            <XAxis dataKey="name" stroke="#888" fontSize={12} />
                            <YAxis yAxisId="left" stroke="#888" fontSize={12} />
                            <YAxis yAxisId="right" orientation="right" stroke="#666" fontSize={12} />
