                            <Legend wrapperStyle={{fontSize: "12px", color: '#888'}} />
                            <Line yAxisId="left" type="monotone" dataKey="Revenue" stroke="#ffffff" strokeWidth={2} />
                            <Line yAxisId="right" type="monotone" dataKey="Points" stroke="#666" strokeDasharray="5 5" />
                        </LineChart>
                    </ResponsiveContainer>
                </Card>
                <Card className="h-[350px]">
                     <h3 className="chart-title">Customer Segments</h3>
                     <ResponsiveContainer width="100%" height="90%">
                        <PieChart>
                            <Pie data={analyticsData.segmentChartData} cx="50%" cy="50%" innerRadius={60} outerRadius={80} fill="#8884d8" paddingAngle={5} dataKey="value" nameKey="name">
