const SettingsSection: React.FC<{ tierSettings: TierSettings, setTierSettings: (settings: TierSettings) => void }> = ({ tierSettings, setTierSettings }) => {
    const [settings, setSettings] = useState(tierSettings);

    const handleInputChange = (tier: 'silver' | 'gold' | 'platinum', field: 'minSpend' | 'minPoints', value: string) => {
        const numValue = parseInt(value.replace(/,/g, ''), 10) || 0;
        setSettings(prev => ({
            ...prev,
            [tier]: {
                ...prev[tier],
                [field]: numValue
            }
        }));
    };

    const handleSaveChanges = () => {
        setTierSettings(settings);
        alert('Settings saved successfully!');
    };

    const TierInputRow: React.FC<{ tier: 'silver' | 'gold' | 'platinum', label: string }> = ({ tier, label }) => (
        <div className="border-b border-brand-border py-4 last:border-b-0">
            <h3 className="text-lg font-semibold capitalize mb-3">{label}</h3>
             <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                    <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Minimum Spend (₹)</label>
                    <input
                        type="text"
                        // FIX: Explicitly cast to Number to resolve incorrect type inference.
                        value={Number(settings[tier].minSpend).toLocaleString()}
                        onChange={(e) => handleInputChange(tier, 'minSpend', e.target.value)}
                        className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors"
                    />
                </div>
                 <div>
                    <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Minimum Points</label>
                    <input
                        type="text"
                        // FIX: Explicitly cast to Number to resolve incorrect type inference.
                        value={Number(settings[tier].minPoints).toLocaleString()}
                        onChange={(e) => handleInputChange(tier, 'minPoints', e.target.value)}
                        className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors"
                    />
                </div>
            </div>
        </div>
    );

    return (
        <section>
            <PageTitle>Customer Tier Settings</PageTitle>
            <Card className="max-w-3xl mx-auto">
                <p className="text-brand-muted mb-6">Define the minimum spend and points required for customers to reach each tier. Customers who don't meet any criteria are automatically assigned to Bronze.</p>
                <div className="space-y-4">
                    <TierInputRow tier="silver" label="Silver Tier" />
                    <TierInputRow tier="gold" label="Gold Tier" />
                    <TierInputRow tier="platinum" label="Platinum Tier" />
                </div>
                <div className="mt-8 text-right">
                    <button onClick={handleSaveChanges} className="bg-brand-accent text-brand-bg py-3 px-8 rounded-md font-semibold transition-colors hover:bg-neutral-300">
                        Save Changes
                    </button>
                </div>
            </Card>
        </section>
    );
};

