import React, { useState, useMemo, useCallback, useEffect, useRef } from 'react';
import { Customer, TransactionHistory, Tier, TierSettings, Admin, TierThreshold } from '../types';
import { FaBars, FaChartLine, FaPlus, FaSearch, FaUsers, FaChartPie, FaPowerOff, FaWallet, FaCoins, FaShoppingBag, FaDownload, FaCog } from 'react-icons/fa';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';

// Tier utility functions
export const getCustomerTier = (customer: Customer, settings: TierSettings): Tier => {
    if (customer.totalSpent >= settings.platinum.minSpend && customer.points >= settings.platinum.minPoints) {
        return 'Platinum';
    }
    if (customer.totalSpent >= settings.gold.minSpend && customer.points >= settings.gold.minPoints) {
        return 'Gold';
    }
    if (customer.totalSpent >= settings.silver.minSpend && customer.points >= settings.silver.minPoints) {
        return 'Silver';
    }
    return 'Bronze';
};

const TIER_COLORS: Record<Tier, string> = {
    Bronze: 'bg-yellow-800 text-yellow-100',
    Silver: 'bg-gray-400 text-black',
    Gold: 'bg-yellow-400 text-black',
    Platinum: 'bg-purple-300 text-purple-900',
};


enum Section {
    Overview = 'overview',
    Transaction = 'transaction',
    Search = 'search',
    Customers = 'customers',
    Analytics = 'analytics',
    Settings = 'settings'
}

interface DashboardProps {
    currentUser: Admin | null;
    customers: Customer[];
    setCustomers: React.Dispatch<React.SetStateAction<Customer[]>>;
    tierSettings: TierSettings;
    setTierSettings: React.Dispatch<React.SetStateAction<TierSettings>>;
    handleLogout: () => void;
}

// Reusable Card Component
const Card: React.FC<{ children: React.ReactNode, className?: string }> = ({ children, className = '' }) => (
    <div className={`bg-brand-card border border-brand-border p-6 md:p-8 transition-colors hover:border-neutral-600 ${className}`}>
        {children}
    </div>
);

// Reusable Stat Card Component
const StatCard: React.FC<{ label: string; value: string | number }> = ({ label, value }) => (
    <Card>
        <span className="block text-xs text-brand-muted uppercase mb-2">{label}</span>
        <div className="text-4xl font-serif text-brand-accent">{value}</div>
    </Card>
);

// Helper to export CSV
const exportDataToCsv = (customers: Customer[]) => {
    let csv = "Name,Mobile,TotalSpent,Points\n";
    customers.forEach(c => {
        csv += `${c.name},${c.mobile},${c.totalSpent},${c.points}\n`;
    });
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'loyalty_data.csv';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
};


const Dashboard: React.FC<DashboardProps> = ({ currentUser, customers, setCustomers, tierSettings, setTierSettings, handleLogout }) => {
    const [activeSection, setActiveSection] = useState<Section>(Section.Overview);
    const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);

    const [activeContentKey, setActiveContentKey] = useState(0);

    const handleNavClick = (section: Section) => {
        setActiveSection(section);
        setActiveContentKey(prev => prev + 1); // Force re-mount for animation
    };

    const dashboardStats = useMemo(() => {
        const totalCustomers = customers.length;
        const totalRevenue = customers.reduce((acc, c) => acc + (c.totalSpent || 0), 0);
        const totalPoints = customers.reduce((acc, c) => acc + (c.points || 0), 0);
        return { totalCustomers, totalRevenue, totalPoints };
    }, [customers]);

    const renderSection = () => {
        switch (activeSection) {
            case Section.Transaction:
                return <TransactionSection customers={customers} setCustomers={setCustomers} />;
            case Section.Search:
                return <SearchSection customers={customers} tierSettings={tierSettings}/>;
            case Section.Customers:
                return <CustomersSection customers={customers} tierSettings={tierSettings}/>;
            case Section.Analytics:
                return <AnalyticsSection customers={customers} tierSettings={tierSettings}/>;
            case Section.Settings:
                return <SettingsSection tierSettings={tierSettings} setTierSettings={setTierSettings} />;
            case Section.Overview:
            default:
                return <OverviewSection stats={dashboardStats} />;
        }
    };

    return (
        <div className="min-h-screen flex flex-col">
            <header className="h-[60px] bg-brand-card/80 backdrop-blur-lg border-b border-brand-border flex justify-between items-center px-5 fixed top-0 left-0 w-full z-50">
                <div className="flex items-center gap-5">
                    <button onClick={() => setIsSidebarCollapsed(!isSidebarCollapsed)} className="text-white text-lg p-1">
                        <FaBars />
                    </button>
                    <div className="font-serif text-lg tracking-wider">Loyalty <span className="italic">Pro</span></div>
                </div>
                <div className="flex items-center gap-3">
                    <div className="border border-brand-border py-1 px-4 rounded-full text-xs tracking-widest hidden sm:block">
                        {currentUser?.businessName || 'Admin'}
                    </div>
                    <button onClick={handleLogout} className="bg-neutral-800 text-white border border-neutral-700 text-xs py-1.5 px-4 rounded-md flex items-center gap-2 hover:border-neutral-500 hover:bg-neutral-700 transition-colors">
                        <FaPowerOff />
                        <span className="hidden md:inline">Exit</span>
                    </button>
                </div>
            </header>

            <div className="flex pt-[60px] flex-1">
                <aside className={`bg-brand-card border-r border-brand-border h-[calc(100vh-60px)] fixed top-[60px] transition-all duration-500 ease-[cubic-bezier(0.16,1,0.3,1)] z-40 ${isSidebarCollapsed ? 'w-[60px]' : 'w-[240px]'}`}>
                    <nav className="mt-4">
                        {(Object.values(Section)).map(section => {
                            if (section === Section.Settings && !currentUser) {
                                return null;
                            }
                            return (
                                <NavItem
                                    key={section}
                                    section={section}
                                    activeSection={activeSection}
                                    isCollapsed={isSidebarCollapsed}
                                    onClick={handleNavClick}
                                />
                            )
                        })}
                    </nav>
                </aside>

                <main className={`flex-1 transition-all duration-500 ease-[cubic-bezier(0.16,1,0.3,1)] p-4 sm:p-6 md:p-10 ${isSidebarCollapsed ? 'ml-[60px]' : 'ml-[240px]'}`}>
                    <div key={activeContentKey} className="animate-fade-slide-up" style={{ animationDuration: '0.6s' }}>
                        {renderSection()}
                    </div>
                </main>
            </div>
        </div>
    );
};

const navIcons: { [key in Section]: React.ElementType } = {
    [Section.Overview]: FaChartLine,
    [Section.Transaction]: FaPlus,
    [Section.Search]: FaSearch,
    [Section.Customers]: FaUsers,
    [Section.Analytics]: FaChartPie,
    [Section.Settings]: FaCog,
};

const NavItem: React.FC<{ section: Section, activeSection: Section, isCollapsed: boolean, onClick: (s: Section) => void }> = ({ section, activeSection, isCollapsed, onClick }) => {
    const Icon = navIcons[section];
    const isActive = section === activeSection;

    return (
        <a
            href="#"
            onClick={(e) => { e.preventDefault(); onClick(section); }}
            className={`flex items-center h-16 text-brand-muted hover:text-brand-accent hover:bg-neutral-900 transition-all border-l-2 ${isActive ? 'border-brand-accent text-brand-accent bg-neutral-900' : 'border-transparent'}`}
        >
            <div className="w-[60px] flex justify-center items-center text-lg"><Icon /></div>
            <span className={`capitalize whitespace-nowrap transition-opacity duration-300 ${isCollapsed ? 'opacity-0' : 'opacity-100'}`}>{section}</span>
        </a>
    );
};

const PageTitle: React.FC<{ children: React.ReactNode, className?: string }> = ({ children, className }) => (
    <h1 className={`font-serif text-3xl md:text-4xl mb-8 pb-5 border-b border-brand-border ${className}`}>{children}</h1>
);


const OverviewSection: React.FC<{ stats: { totalCustomers: number; totalRevenue: number; totalPoints: number; } }> = ({ stats }) => (
    <section>
        <PageTitle>System Overview</PageTitle>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            <StatCard label="Total Users" value={stats.totalCustomers.toLocaleString()} />
            <StatCard label="Revenue" value={`₹${stats.totalRevenue.toLocaleString()}`} />
            <StatCard label="Active Points" value={stats.totalPoints.toLocaleString()} />
        </div>
    </section>
);


const TransactionSection: React.FC<{ customers: Customer[], setCustomers: React.Dispatch<React.SetStateAction<Customer[]>> }> = ({ customers, setCustomers }) => {
    const [mobile, setMobile] = useState('');
    const [name, setName] = useState('');
    const [pin, setPin] = useState('');
    const [billAmount, setBillAmount] = useState('');
    const [cashGiven, setCashGiven] = useState('');

    const [currentCustomer, setCurrentCustomer] = useState<Customer | null>(null);
    const [isNewCustomer, setIsNewCustomer] = useState(false);

    const [showPinSection, setShowPinSection] = useState(false);
    const [showTransactionForm, setShowTransactionForm] = useState(false);

    const [pinStatus, setPinStatus] = useState('');
    const [pinError, setPinError] = useState('');

    const custNameRef = useRef<HTMLInputElement>(null);

    const resetForm = useCallback(() => {
        setMobile('');
        setName('');
        setPin('');
        setBillAmount('');
        setCashGiven('');
        setCurrentCustomer(null);
        setIsNewCustomer(false);
        setShowPinSection(false);
        setShowTransactionForm(false);
        setPinStatus('');
        setPinError('');
    }, []);

    useEffect(() => {
        if(mobile.length >= 10) {
            const existingCustomer = customers.find(c => c.mobile === mobile);
            setCurrentCustomer(existingCustomer || null);
            setShowPinSection(true);
            setShowTransactionForm(false);
            if (existingCustomer) {
                setName(existingCustomer.name);
                setIsNewCustomer(false);
                setPinStatus("Existing User Verification");
            } else {
                setName('');
                setIsNewCustomer(true);
                setPinStatus("Set New User PIN");
                custNameRef.current?.focus();
            }
        } else {
            setShowPinSection(false);
            setShowTransactionForm(false);
            setCurrentCustomer(null);
            setName('');
        }
    }, [mobile, customers]);

    const handlePinVerify = () => {
        setPinError('');
        if (isNewCustomer) {
            if (pin.length === 4) {
                setShowPinSection(false);
                setShowTransactionForm(true);
            } else {
                setPinError("PIN must be 4 digits.");
            }
        } else {
            if (currentCustomer?.pin === pin) {
                setShowPinSection(false);
                setShowTransactionForm(true);
            } else {
                setPinError("Incorrect PIN code.");
            }
        }
    };

    const handleTransactionSubmit = () => {
        const bill = parseFloat(billAmount);
        const cash = parseFloat(cashGiven);

        if (!bill || bill <= 0) { alert("Please enter a valid Bill Amount"); return; }
        if (!cash || cash < bill) { alert("Cash Given cannot be less than Bill Amount"); return; }

        const earnedPoints = Math.floor(cash - bill);
        const newHistoryEntry: TransactionHistory = { date: new Date().toISOString(), bill, points: earnedPoints };

        if (isNewCustomer) {
            const newCustomer: Customer = {
                mobile,
                name: name || "Guest",
                pin,
                points: earnedPoints,
                totalSpent: bill,
                history: [newHistoryEntry]
            };
            setCustomers(prev => [...prev, newCustomer]);
        } else {
             setCustomers(prev => prev.map(c => 
                c.mobile === mobile 
                    ? { 
                        ...c, 
                        points: c.points + earnedPoints, 
                        totalSpent: c.totalSpent + bill,
                        history: [...(c.history || []), newHistoryEntry]
                      } 
                    : c
            ));
        }

        alert(`Transaction Successful! Added ${earnedPoints} Points.`);
        resetForm();
    };

    const pointsPreview = useMemo(() => {
        const bill = parseFloat(billAmount);
        const cash = parseFloat(cashGiven);
        if (cash >= bill && bill > 0) {
            return Math.floor(cash - bill);
        }
        return 0;
    }, [billAmount, cashGiven]);

    return (
        <section>
            <PageTitle>New Entry</PageTitle>
            <Card className="max-w-xl mx-auto">
                <div className="space-y-6">
                    <div>
                        <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Mobile Number</label>
                        <input type="text" value={mobile} onChange={e => setMobile(e.target.value)} placeholder="Enter 10-digit mobile number..." className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors" />
                    </div>
                    <div>
                        <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Name</label>
                        <input type="text" ref={custNameRef} value={name} onChange={e => setName(e.target.value)} placeholder="Customer Name" readOnly={!isNewCustomer && !!currentCustomer} className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors" />
                    </div>
                </div>

                {showPinSection && (
                    <div className="mt-6 p-4 border border-dashed border-brand-border bg-black/20 animate-fade-slide-up">
                        <div className="flex justify-between items-center mb-2">
                            <label className="text-sm text-brand-text">Security PIN</label>
                            <span className="text-xs text-brand-muted">{pinStatus}</span>
                        </div>
                        <div className="flex gap-4">
                            <input type="password" value={pin} onChange={e => setPin(e.target.value)} maxLength={4} placeholder="••••" className="flex-grow bg-transparent border-b border-brand-border py-2 text-white text-lg tracking-[0.5em] text-center outline-none focus:border-brand-accent transition-colors" />
                            <button onClick={handlePinVerify} className="bg-neutral-800 text-white border border-neutral-700 text-xs py-1.5 px-4 rounded-md hover:border-neutral-500 hover:bg-neutral-700 transition-colors">Verify</button>
                        </div>
                        {pinError && <p className="text-red-500 text-xs mt-2">{pinError}</p>}
                    </div>
                )}

                {showTransactionForm && (
                    <div className="mt-6 space-y-6 animate-fade-slide-up">
                        <div className="grid grid-cols-2 gap-6">
                             <div>
                                <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Bill Amount (₹)</label>
                                <input type="number" value={billAmount} onChange={e => setBillAmount(e.target.value)} placeholder="e.g. 900" className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors" />
                            </div>
                             <div>
                                <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Cash Given (₹)</label>
                                <input type="number" value={cashGiven} onChange={e => setCashGiven(e.target.value)} placeholder="e.g. 1000" className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors" />
                            </div>
                        </div>
                        <div className="bg-black/30 p-4 border border-brand-border flex justify-between items-center">
                            <span className="text-xs text-brand-muted uppercase tracking-wider">Points to Add</span>
                            <span className="text-2xl font-bold text-white">{pointsPreview}</span>
                        </div>
                        <button onClick={handleTransactionSubmit} className="w-full mt-2 bg-brand-accent text-brand-bg border border-brand-accent py-3 font-semibold transition-all hover:bg-neutral-300">
                            Process Transaction
                        </button>
                    </div>
                )}
            </Card>
        </section>
    );
};

