const AnalyticsSection: React.FC<{ customers: Customer[], tierSettings: TierSettings }> = ({ customers, tierSettings }) => {
    const analyticsData = useMemo(() => {
        let totalRevenue = 0;
        let totalPoints = 0;
        let totalTxns = 0;
        customers.forEach(c => {
            totalRevenue += c.totalSpent || 0;
            totalPoints += c.points || 0;
            totalTxns += c.history?.length || 0;
        });
        const aov = totalTxns > 0 ? Math.round(totalRevenue / totalTxns) : 0;

        const tierCounts = customers.reduce((acc, c) => {
            const tier = getCustomerTier(c, tierSettings);
            acc[tier] = (acc[tier] || 0) + 1;
            return acc;
        }, {} as Record<Tier, number>);

        const pieData = Object.entries(tierCounts).map(([name, value]) => ({ name, value }));

        const topCustomers = [...customers].sort((a, b) => b.totalSpent - a.totalSpent).slice(0, 5);

        const revenueHistory = customers.flatMap(c => c.history || []).sort((a,b) => new Date(a.date).getTime() - new Date(b.date).getTime());
        const aggregatedRevenue = revenueHistory.reduce((acc, curr) => {
            const date = new Date(curr.date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
            if(!acc[date]){
                acc[date] = 0;
            }
            acc[date] += curr.finalBill ?? curr.bill; // Use finalBill for accuracy, fallback to bill for old data
            return acc;
        }, {} as Record<string, number>);

        const lineChartData = Object.entries(aggregatedRevenue).map(([date, revenue]) => ({ date, revenue })).slice(-30);

        return { aov, totalPoints, totalTxns, pieData, topCustomers, lineChartData };
    }, [customers, tierSettings]);

    const PIE_CHART_COLORS: Record<Tier, string> = { Bronze: '#8d5b2d', Silver: '#a9a9a9', Gold: '#ffd700', Platinum: '#e5e4e2' };
    const pieColors = analyticsData.pieData.map(entry => PIE_CHART_COLORS[entry.name as Tier]);

    return (
        <section>
            <div className="flex justify-between items-end mb-8 pb-5 border-b border-brand-border">
                <h1 className="font-serif text-3xl md:text-4xl mb-0">Intelligence Hub</h1>
                <button onClick={() => exportDataToCsv(customers)} className="bg-neutral-800 text-white border border-neutral-700 text-xs py-1.5 px-4 rounded-md flex items-center gap-2 hover:border-neutral-500 hover:bg-neutral-700 transition-colors">
                    <FaDownload /> Export CSV
                </button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
                <Card className="flex items-center gap-4"><div className="text-2xl p-3 bg-white/5 rounded-full"><FaWallet /></div><div><span className="block text-xs text-brand-muted uppercase">Avg. Order Value</span><div className="text-2xl font-serif">₹{analyticsData.aov.toLocaleString()}</div></div></Card>
                <Card className="flex items-center gap-4"><div className="text-2xl p-3 bg-white/5 rounded-full"><FaCoins /></div><div><span className="block text-xs text-brand-muted uppercase">Points Liability</span><div className="text-2xl font-serif">{analyticsData.totalPoints.toLocaleString()}</div></div></Card>
                <Card className="flex items-center gap-4"><div className="text-2xl p-3 bg-white/5 rounded-full"><FaShoppingBag /></div><div><span className="block text-xs text-brand-muted uppercase">Total Transactions</span><div className="text-2xl font-serif">{analyticsData.totalTxns.toLocaleString()}</div></div></Card>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-6">
                <Card className="lg:col-span-2 h-[400px]">
                     <h3 className="font-serif text-lg text-brand-muted mb-4">Revenue Velocity</h3>
                    <ResponsiveContainer width="100%" height="90%">
                        <LineChart data={analyticsData.lineChartData} margin={{ top: 5, right: 20, left: -10, bottom: 5 }}>
                            <CartesianGrid strokeDasharray="3 3" stroke="#333" />
                            <XAxis dataKey="date" stroke="#888" fontSize={12} />
                            <YAxis stroke="#888" fontSize={12} tickFormatter={(value) => `₹${Number(value)/1000}k`} />
                            <Tooltip contentStyle={{ backgroundColor: '#0a0a0a', border: '1px solid #333' }} />
