                 </div>
                 <div>
                    <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Min. Points</label>
                    <input type="text" pattern="\d*" value={formState[tier].minPoints} onChange={(e) => handleInputChange(tier, 'minPoints', e.target.value)} className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors" />
                </div>
            </div>
        </div>
    );

    return (
        <Card>
            <div className="space-y-8">
                <h2 className="font-serif text-xl text-brand-muted pb-3 border-b border-brand-border">Customer Tier Settings</h2>
                <p className="text-sm text-brand-muted -mt-4">A customer achieves a tier if they meet <span className="font-bold text-brand-text">either</span> the minimum spend <span className="font-bold text-brand-text">or</span> minimum points requirement.</p>
                <TierSettingInput tier="bronze" tierName="Bronze" />
                <TierSettingInput tier="silver" tierName="Silver" />
                <TierSettingInput tier="gold" tierName="Gold" />
                <TierSettingInput tier="platinum" tierName="Platinum" />
                <div className="flex justify-end pt-4">
                    <button onClick={handleSaveChanges} className={`bg-brand-accent text-brand-bg border border-brand-accent py-2 px-6 font-semibold transition-all ${saved ? 'bg-green-500 border-green-500' : 'hover:bg-neutral-300'}`}>
