// Helper to export CSV
const exportDataToCsv = (customers: Customer[]) => {
    let csv = "Name,Mobile,TotalSpent,Points\n";
    customers.forEach(c => {
        csv += `${c.name},${c.mobile},${c.totalSpent},${c.points}\n`;
    });
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'loyalty_data.csv';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
};


const Dashboard: React.FC<DashboardProps> = ({ currentUser, customers, setCustomers, tierSettings, setTierSettings, discountSettings, setDiscountSettings, handleLogout }) => {
