const TransactionSection: React.FC<{ customers: Customer[], setCustomers: React.Dispatch<React.SetStateAction<Customer[]>>, tierSettings: TierSettings, discountSettings: DiscountSettings }> = ({ customers, setCustomers, tierSettings, discountSettings }) => {
    const [mobile, setMobile] = useState('');
    const [name, setName] = useState('');
    const [pin, setPin] = useState('');
    const [billAmount, setBillAmount] = useState('');
    const [cashGiven, setCashGiven] = useState('');

    const [currentCustomer, setCurrentCustomer] = useState<Customer | null>(null);
    const [isNewCustomer, setIsNewCustomer] = useState(false);

    const [showPinSection, setShowPinSection] = useState(false);
    const [showTransactionForm, setShowTransactionForm] = useState(false);
    const [canUsePoints, setCanUsePoints] = useState(false);
    const [usePointsAndDiscount, setUsePointsAndDiscount] = useState(false);

    const [pinStatus, setPinStatus] = useState('');
    const [pinError, setPinError] = useState('');

    const custNameRef = useRef<HTMLInputElement>(null);

    const resetForm = useCallback(() => {
        setMobile('');
        setName('');
        setPin('');
        setBillAmount('');
        setCashGiven('');
        setCurrentCustomer(null);
        setIsNewCustomer(false);
        setShowPinSection(false);
        setShowTransactionForm(false);
        setPinStatus('');
        setPinError('');
        setCanUsePoints(false);
        setUsePointsAndDiscount(false);
    }, []);

    useEffect(() => {
        setCanUsePoints(false);
        setUsePointsAndDiscount(false);
        if(mobile.length >= 10) {
            const existingCustomer = customers.find(c => c.mobile === mobile);
            setCurrentCustomer(existingCustomer || null);
            setShowPinSection(true);
            setShowTransactionForm(false);
            if (existingCustomer) {
                setName(existingCustomer.name);
                setIsNewCustomer(false);
                setPinStatus("Existing User Verification");
                if (existingCustomer.points > 0) {
                    setCanUsePoints(true);
                }
            } else {
                setName('');
                setIsNewCustomer(true);
                setPinStatus("Set New User PIN");
                custNameRef.current?.focus();
            }
        } else {
            setShowPinSection(false);
            setShowTransactionForm(false);
            setCurrentCustomer(null);
            setName('');
        }
    }, [mobile, customers]);

    const handlePinVerify = () => {
        setPinError('');
        if (isNewCustomer) {
            if (pin.length === 4) {
                setShowPinSection(false);
                setShowTransactionForm(true);
            } else {
                setPinError("PIN must be 4 digits.");
            }
        } else {
            if (currentCustomer?.pin === pin) {
                setShowPinSection(false);
                setShowTransactionForm(true);
            } else {
                setPinError("Incorrect PIN code.");
            }
        }
    };

    const tierInfo = useMemo(() => {
        if (!currentCustomer) return null;
        const spendTier = getTierBySpend(currentCustomer, tierSettings);
        const pointsTier = getTierByPoints(currentCustomer, tierSettings);
        const effectiveTier = getCustomerTier(currentCustomer, tierSettings);
        return { spendTier, pointsTier, effectiveTier };
    }, [currentCustomer, tierSettings]);

    const billDetails = useMemo(() => {
        const subtotal = parseFloat(billAmount) || 0;
        let discountPercentage = 0;
        let discountAmount = 0;
        let finalBill = subtotal; // After discount
        let pointsUsed = 0;
        let cashPayable = finalBill;

        if (usePointsAndDiscount && currentCustomer && currentCustomer.points > 0 && subtotal > 0 && tierInfo) {
            discountPercentage = discountSettings[tierInfo.effectiveTier.toLowerCase() as keyof DiscountSettings] || 0;
            discountAmount = subtotal * (discountPercentage / 100);
            finalBill = subtotal - discountAmount;
            pointsUsed = Math.min(currentCustomer.points, finalBill);
            cashPayable = finalBill - pointsUsed;
        }

        const cash = parseFloat(cashGiven) || 0;
        const pointsEarned = Math.max(0, Math.floor(cash - cashPayable));

        return { subtotal, discountPercentage, discountAmount, finalBill, pointsUsed, cashPayable, pointsEarned };
    }, [billAmount, cashGiven, usePointsAndDiscount, currentCustomer, tierSettings, discountSettings, tierInfo]);


    const handleTransactionSubmit = () => {
        const { subtotal, discountPercentage, finalBill, pointsUsed, cashPayable, pointsEarned } = billDetails;
        const cash = parseFloat(cashGiven);

        if (!subtotal || subtotal <= 0) { alert("Please enter a valid Bill Amount"); return; }
        if (cash < cashPayable) { alert("Cash Given cannot be less than the amount payable."); return; }

        const newHistoryEntry: TransactionHistory = { 
            date: new Date().toISOString(), 
            bill: subtotal,
            discountPercentage: usePointsAndDiscount ? discountPercentage : undefined,
            finalBill,
            pointsUsed: usePointsAndDiscount ? pointsUsed : 0,
            points: pointsEarned,
        };

        if (isNewCustomer) {
            const newCustomer: Customer = { mobile, name: name || "Guest", pin, points: pointsEarned, totalSpent: finalBill, history: [newHistoryEntry] };
            setCustomers(prev => [...prev, newCustomer]);
        } else {
             setCustomers(prev => prev.map(c => {
                if (c.mobile !== mobile) return c;
                const finalPoints = (c.points - pointsUsed) + pointsEarned;
                return { ...c, points: finalPoints, totalSpent: c.totalSpent + finalBill, history: [...(c.history || []), newHistoryEntry] };
             }));
        }

        alert(`Transaction Successful! Added ${pointsEarned} Points.`);
        resetForm();
    };

    return (
        <section>
            <PageTitle>New Entry</PageTitle>
            <Card className="max-w-xl mx-auto">
                <div className="space-y-6">
                    <div>
                        <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Mobile Number</label>
                        <input type="text" value={mobile} onChange={e => setMobile(e.target.value)} placeholder="Enter 10-digit mobile number..." className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors" />
                    </div>
                    <div>
                        <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Name</label>
                        <input type="text" ref={custNameRef} value={name} onChange={e => setName(e.target.value)} placeholder="Customer Name" readOnly={!isNewCustomer && !!currentCustomer} className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors" />
                    </div>
                </div>

                {showPinSection && (
                    <div className="mt-6 p-4 border border-dashed border-brand-border bg-black/20 animate-fade-slide-up">
                        <div className="flex justify-between items-center mb-2">
                            <label className="text-sm text-brand-text">Security PIN</label>
                            <span className="text-xs text-brand-muted">{pinStatus}</span>
                        </div>
                        <div className="flex gap-4">
                            <input type="password" value={pin} onChange={e => setPin(e.target.value)} maxLength={4} placeholder="••••" className="flex-grow bg-transparent border-b border-brand-border py-2 text-white text-lg tracking-[0.5em] text-center outline-none focus:border-brand-accent transition-colors" />
                            <button onClick={handlePinVerify} className="bg-neutral-800 text-white border border-neutral-700 text-xs py-1.5 px-4 rounded-md hover:border-neutral-500 hover:bg-neutral-700 transition-colors">Verify</button>
                        </div>
                        {pinError && <p className="text-red-500 text-xs mt-2">{pinError}</p>}
                    </div>
                )}

                {showTransactionForm && (
                    <div className="mt-6 space-y-6 animate-fade-slide-up text-sm">
                         <div className="grid grid-cols-2 gap-6">
                            <div>
                               <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Bill Amount (₹)</label>
                               <input type="number" value={billAmount} onChange={e => setBillAmount(e.target.value)} placeholder="e.g. 900" className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors" />
                           </div>
                            <div>
                               <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">Amount Given (₹)</label>
                               <input type="number" value={cashGiven} onChange={e => setCashGiven(e.target.value)} placeholder="e.g. 1000" className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors" />
                           </div>
                        </div>

                        {tierInfo && (
                             <div className="p-4 bg-black/20 border border-brand-border/50 flex justify-between items-center">
                                <div>
                                    <h4 className="font-bold text-brand-text uppercase tracking-wider">Effective Tier</h4>
                                    <p className="text-xs text-brand-muted mt-1">Based on the higher of Spending Tier ({tierInfo.spendTier}) and Points Tier ({tierInfo.pointsTier}).</p>
                                </div>
                                <span className={`text-xs px-3 py-1 rounded-full font-bold ${TIER_COLORS[tierInfo.effectiveTier]}`}>{tierInfo.effectiveTier}</span>
                            </div>
                        )}

                        {canUsePoints && currentCustomer && (
                            <div className="p-4 bg-black/20 border border-brand-border/50 flex justify-between items-center">
                                <div>
                                    <h4 className="font-bold text-brand-text">Redeem Points & Get Tier Discount</h4>
                                    <p className="text-xs text-brand-muted mt-1">Available: <span className="font-bold text-white">{currentCustomer.points.toLocaleString()}</span> points</p>
                                </div>
                                <input id="redeem-points-checkbox" type="checkbox" checked={usePointsAndDiscount} onChange={e => setUsePointsAndDiscount(e.target.checked)} className="h-5 w-5 rounded bg-brand-card border-brand-border text-brand-accent focus:ring-brand-accent shrink-0" />
                            </div>
                        )}

                        <div className="p-4 border border-dashed border-brand-border/50 space-y-2">
                             <h4 className="text-center font-serif text-lg mb-4">Bill Summary</h4>
                             <div className="flex justify-between items-center text-brand-muted">
                                 <span>Subtotal</span>
                                 <span>₹{billDetails.subtotal.toFixed(2)}</span>
                             </div>
                             {billDetails.discountAmount > 0 && (
                                <div className="flex justify-between items-center text-green-400">
                                     <span>{tierInfo?.effectiveTier} Discount ({billDetails.discountPercentage}%)</span>
                                     <span>-₹{billDetails.discountAmount.toFixed(2)}</span>
                                 </div>
                             )}
                            {billDetails.pointsUsed > 0 && (
                                <div className="flex justify-between items-center text-brand-muted">
                                    <span>Paid by Points</span>
                                    <span>-₹{billDetails.pointsUsed.toFixed(2)}</span>
                                </div>
                             )}
                             <div className="flex justify-between items-center text-brand-text font-bold text-lg border-t border-brand-border/50 pt-2 mt-2">
                                 <span>Total Payable</span>
                                 <span>₹{billDetails.cashPayable.toFixed(2)}</span>
                             </div>
                             {billDetails.pointsEarned > 0 && (
                                 <p className="text-center text-green-400 pt-2 font-bold">+ {billDetails.pointsEarned} Points Earned</p>
                             )}
                        </div>

                        <button onClick={handleTransactionSubmit} className="w-full mt-2 bg-brand-accent text-brand-bg border border-brand-accent py-3 font-semibold transition-all hover:bg-neutral-300">
                            Process Transaction
                        </button>
                    </div>
                )}
            </Card>
        </section>
    );
};

const SearchSection: React.FC<{ customers: Customer[], tierSettings: TierSettings }> = ({ customers, tierSettings }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [foundCustomer, setFoundCustomer] = useState<Customer | null>(null);
    const [notFound, setNotFound] = useState(false);

    const handleSearch = () => {
        const customer = customers.find(c => c.mobile === searchTerm);
