                <DiscountInput tier="bronze" tierName="Bronze" />
                <DiscountInput tier="silver" tierName="Silver" />
                <DiscountInput tier="gold" tierName="Gold" />
                <DiscountInput tier="platinum" tierName="Platinum" />
                <div className="flex justify-end pt-4">
                    <button onClick={handleSaveChanges} className={`bg-brand-accent text-brand-bg border border-brand-accent py-2 px-6 font-semibold transition-all ${saved ? 'bg-green-500 border-green-500' : 'hover:bg-neutral-300'}`}>
                        {saved ? 'Saved!' : 'Save Changes'}
                    </button>
                </div>
            </div>
        </Card>
    );
};

