}

const AuthPage: React.FC<AuthPageProps> = ({ onLogin, onRegister, onDevLogin }) => {
    const [isLoginView, setIsLoginView] = useState(true);

    // Login state
    const [loginUsername, setLoginUsername] = useState('');
    const [loginPassword, setLoginPassword] = useState('');
    const [loginError, setLoginError] = useState('');

    // Register state
    const [regBusinessName, setRegBusinessName] = useState('');
    const [regUsername, setRegUsername] = useState('');
    const [regPassword, setRegPassword] = useState('');
    const [regConfirmPassword, setRegConfirmPassword] = useState('');
    const [regError, setRegError] = useState('');

    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);


    const handleLoginSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        setLoginError('');
        const success = onLogin(loginUsername, loginPassword);
        if (!success) {
            setLoginError('Invalid username or password.');
        }
    };

    const handleRegisterSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        setRegError('');
        if (regPassword !== regConfirmPassword) {
            setRegError('Passwords do not match.');
            return;
        }
        if (!regBusinessName || !regUsername || !regPassword) {
            setRegError('All fields are required.');
            return;
        }
        const success = onRegister(regBusinessName, regUsername, regPassword);
        if (!success) {
            setRegError('Username already exists.');
        }
    };

    const InputField = ({ label, type = 'text', value, onChange, placeholder, children }: { label: string, type?: string, value: string, onChange: (e: React.ChangeEvent<HTMLInputElement>) => void, placeholder: string, children?: React.ReactNode }) => (
        <div>
            <label className="block text-xs text-brand-muted mb-2 uppercase tracking-wider">{label}</label>
            <div className="relative">
                <input
                    type={type}
                    value={value}
                    onChange={onChange}
                    placeholder={placeholder}
                    className="w-full bg-transparent border-b border-brand-border py-2 text-white text-base outline-none focus:border-brand-accent transition-colors pr-8"
                    required
                />
                {children}
            </div>
        </div>
    );

    return (
        <div className="min-h-screen w-full flex flex-col items-center justify-center p-4 bg-brand-bg">
            <div className="text-center mb-8">
                <FaInfinity className="text-4xl text-brand-accent mx-auto mb-4" />
                <h1 className="text-4xl md:text-5xl font-serif">Loyalty <span className="italic">Pro</span></h1>
                <p className="text-brand-muted mt-2 text-sm">Admin Access Terminal</p>
            </div>

            <div className="w-full max-w-sm p-8 border border-brand-border bg-black shadow-2xl animate-fade-slide-up">
                <div className="flex mb-6 border-b border-brand-border">
                    <button onClick={() => setIsLoginView(true)} className={`flex-1 pb-2 text-sm uppercase tracking-wider ${isLoginView ? 'text-white border-b-2 border-white' : 'text-brand-muted'}`}>Login</button>
