                             <button type="button" onClick={() => setShowConfirmPassword(!showConfirmPassword)} className="absolute right-0 top-2 text-brand-muted p-1 hover:text-brand-text">
                                {showConfirmPassword ? <FaEyeSlash /> : <FaEye />}
                            </button>
